import { useAppUIModal } from '@/contexts/AppUIModalContext'
import { useSessionInfo } from '@/contexts/AuthContext/hooks/sessionInfo'
import { devLog } from '@/helpers/log'
import { useIsMounted } from '@/hooks/isMounted'
import { useLocale } from '@/hooks/routes'
import { useCreationRecordsV2 } from '@/hooks/useCreationRecordsV2'
import { reverseTransformPublished } from '@/pages/pollo.ai/_components/Fields/ProFormPublish/helpers'
import { getMediaTypeStatus } from '@/pages/pollo.ai/_helpers/getMediaType'
import { InspirationModalContextProvider } from '@/pages/pollo.ai/home/canvas/_entities/tools/generations/inspirations/store/context'
import { useIsMySelf } from '@/pages/pollo.ai/v/[id]/_hooks/isMySelf'
import { EProjectType } from '@loc/server/constant/userProject'
import { useMemoizedFn } from 'ahooks'
import { Button } from 'antd'

import type { CreationItem } from '@/pages/pollo.ai/_types/video'
import type { IIsShowLoraDetail } from '@/pages/pollo.ai/home/canvas/_entities/tools/generations/inspirations/store/context'
import type { EntryCodeType } from '@loc/server/enums/video.common'
import type { VideoDetailVo } from '@loc/server/interface/vo/video-detail.vo'

import { useUpdateVideoDetail } from '../../_hooks/useVideoDetail'
import { DetailPrimitive } from './primitive'

export interface IVideoDetailActions {
  prevRecord: (videoId: string) => VideoDetailVo | undefined
  currentRecord?: (videoId: string) => VideoDetailVo | undefined
  nextRecord: (videoId: string) => VideoDetailVo | undefined
}

export type TVideoDetailVo = VideoDetailVo & {
  currentSelectedGenerationIndex?: number
}

interface IDetailProps {
  className?: string
  videoDetail: CreationItem
  isModal?: boolean
  actions?: IVideoDetailActions
  currentSelectedGenerationIndex?: number
  isFromCanvas?: boolean
  createSimilarClick?: (params: {
    url: string
    type: string
    generateRecord?: any
    videoDetail?: Partial<CreationItem>
  }) => void
  setIsShowLoraDetail?: (isShowLoraDetail: IIsShowLoraDetail) => void
  useGeneration?: boolean
  openInNewWindow?: boolean
}

const Detail = (props: IDetailProps) => {
  const {
    className,
    videoDetail: firstVideoDetail,
    isModal,
    actions,
    currentSelectedGenerationIndex: defaultCurrentSelectedGenerationIndex,
    isFromCanvas: videoDetailModalInCanvas,
    createSimilarClick: videoDetailModalCreateSimilarClick,
    setIsShowLoraDetail: videoDetailModalSetIsShowLoraDetail,
    useGeneration = true,
    openInNewWindow = false,
  } = props

  const { videoDetailModal } = useAppUIModal()

  const [_videoDetail, setVideoDetail] =
    useState<VideoDetailVo>(firstVideoDetail)

  const locale = useLocale()
  const lang = locale === 'en' ? '' : `/${locale}`

  const [currentSelectedGenerationIndex, setCurrentSelectedGenerationIndex] =
    useState(defaultCurrentSelectedGenerationIndex || 0)

  const { onUpdateVideoDetail: onUpdateVideo, videoDetail } =
    useUpdateVideoDetail(_videoDetail, isModal)

  const selectedGeneration = useMemo(() => {
    return videoDetail?.generations?.[currentSelectedGenerationIndex]
  }, [currentSelectedGenerationIndex, videoDetail])

  const { isMySelf } = useIsMySelf(videoDetail)

  const { isMounted } = useIsMounted()
  const isHasPrev = useMemo(() => {
    if (!isMounted) return false

    return !!actions?.prevRecord(videoDetail.videoId)
  }, [actions, videoDetail.videoId, isMounted])

  const isHasNext = useMemo(() => {
    if (!isMounted) return false

    return !!actions?.nextRecord(videoDetail.videoId)
  }, [actions, videoDetail.videoId, isMounted])

  const { isImage } = getMediaTypeStatus(videoDetail.mediaType)

  const isFromCanvas =
    videoDetail.generateRecord.projectType === EProjectType.Canvas

  const { deleteGenerationInStore } = useCreationRecordsV2()

  const menuOptionsData = useMemo(() => {
    return {
      ...videoDetail,
      videoId: selectedGeneration?.videoId || '',
      videoUrl: selectedGeneration?.videoUrl || '',
      mediaUrl: selectedGeneration?.mediaUrl || '',
      reallyVideoId: videoDetail.videoId || '',
      reallyVideoUrl: videoDetail.videoUrl || '',
      favorite: Boolean(selectedGeneration?.favorite),
      ...reverseTransformPublished({
        publishDate: videoDetail.publishDate,
      }),
      protectionMode: videoDetail.protectionMode ?? false,
    }
  }, [videoDetail, selectedGeneration])

  const updateHistoryVideoId = (videoId: string) => {
    history.replaceState(
      null,
      '',
      `${lang}/v/${videoId}${window.location.search}`,
    )
  }

  const { isSignIn } = useSessionInfo()
  const projectId = videoDetail?.generateRecord?.projectId
  const { data: projectDetail } = api.userProject.getDetail.useQuery(
    { projectId: projectId || '' },
    {
      enabled: !!projectId && isMySelf && isSignIn,
      onError: (error) => {
        devLog('get project info failed:', error)
      },
    },
  )

  // 统一更新 video
  const handleUpdateVideo = useMemoizedFn(
    async (data?: Partial<VideoDetailVo>) => {
      if (data) {
        let newVideoDetail = { ...videoDetail }
        // 如果只有一条数据，当前操作的数据非 generations 中的数据，则外层数据也要更新
        if (newVideoDetail?.generations?.length === 1) {
          newVideoDetail = {
            ...newVideoDetail,
            ...data,
          }
        } else if (
          newVideoDetail?.generations?.find((i) => i.videoId === data.videoId)
        ) {
          newVideoDetail = {
            ...newVideoDetail,
            ...data,
            generations: newVideoDetail.generations.map((item) => {
              return item?.videoId === data.videoId
                ? { ...item, ...data }
                : item
            }),
          }
        }
        await onUpdateVideo?.({ videoData: newVideoDetail })
      }
    },
  )

  const updateFavorite = (videoId: string, favorite: boolean) => {
    handleUpdateVideo({
      videoId,
      favorite,
      generations: videoDetail.generations?.map((i) => {
        if (i.videoId === videoId) {
          return { ...i, favorite }
        }
        return i
      }),
    })
  }

  return (
    <InspirationModalContextProvider
      useCurrentUrlJump={videoDetailModalInCanvas}
      setIsShowLoraDetail={videoDetailModalSetIsShowLoraDetail}
      createSimilarClick={videoDetailModalCreateSimilarClick}
    >
      <DetailPrimitive.Root
        className={className}
        videoDetail={videoDetail}
        projectDetail={projectDetail}
        isMySelf={isMySelf}
        isImage={isImage}
        isFromCanvas={isFromCanvas}
        menuOptionsData={menuOptionsData}
        currentSelectedGenerationIndex={currentSelectedGenerationIndex}
        entryCode={videoDetail.generateRecord?.entryCode as EntryCodeType}
      >
        <DetailPrimitive.Container>
          <DetailPrimitive.Left>
            <DetailPrimitive.LeftTop
              onSelectChange={(index) => {
                const currentVideoId = videoDetail.generations?.[index]?.videoId
                if (currentVideoId && actions?.currentRecord) {
                  const currentVideo = actions.currentRecord(currentVideoId)
                  if (currentVideo) {
                    setVideoDetail(currentVideo)
                  }
                  updateHistoryVideoId(currentVideoId)
                }
                setCurrentSelectedGenerationIndex(index)
              }}
            />
            <DetailPrimitive.LeftBottom updateFavorite={updateFavorite} />
          </DetailPrimitive.Left>

          <DetailPrimitive.Right>
            <DetailPrimitive.RightTop
              openInNewWindow={openInNewWindow}
              onUpdateVideo={handleUpdateVideo}
            />

            <DetailPrimitive.RightBottom
              openInNewWindow={openInNewWindow}
              onUpdateVideo={async ({ videoData }) =>
                handleUpdateVideo(videoData)
              }
              updateFavorite={updateFavorite}
              onDeleteImageSuccess={(generationId) => {
                // 先删除掉当前的generation
                const newGenerations = videoDetail.generations?.filter(
                  (_, index) => index !== currentSelectedGenerationIndex,
                )

                if (!useGeneration) {
                  deleteGenerationInStore(
                    videoDetail.generateRecord.id,
                    generationId,
                  )
                  onUpdateVideo({
                    videoData: {
                      ...videoDetail,
                      generations: newGenerations,
                    },
                    type: 'delete',
                  })

                  const nextIndex = Math.max(
                    0,
                    currentSelectedGenerationIndex - 1,
                  )
                  setCurrentSelectedGenerationIndex(nextIndex)
                  updateHistoryVideoId(
                    newGenerations?.[nextIndex]?.videoId || '',
                  )
                  return
                }

                // 获取到prev和next
                const prev: TVideoDetailVo | undefined = actions?.prevRecord(
                  videoDetail.videoId,
                )
                const next: TVideoDetailVo | undefined = actions?.nextRecord(
                  videoDetail.videoId,
                )
                const showVideoDetail: TVideoDetailVo | undefined = prev || next

                // 通知外部更新 store 数据
                onUpdateVideo({
                  videoData: {
                    ...videoDetail,
                    generations: newGenerations,
                  },
                  type: 'delete',
                })

                // 如果prev和next存在，则优先更新上一条，否则更新下一条，分组删除完后直接关闭 modal
                if (
                  showVideoDetail &&
                  newGenerations?.length &&
                  newGenerations.length > 0
                ) {
                  // 优先更新上一条
                  let newCurrentSelectedGenerationIndex = prev
                    ? prev.currentSelectedGenerationIndex ?? 0
                    : 0
                  if (next && !prev) {
                    if (
                      next.currentSelectedGenerationIndex &&
                      next.currentSelectedGenerationIndex > 0
                    ) {
                      newCurrentSelectedGenerationIndex =
                        next.currentSelectedGenerationIndex - 1
                    } else {
                      newCurrentSelectedGenerationIndex = 0
                    }
                  }
                  setVideoDetail({
                    ...showVideoDetail,
                    generations: showVideoDetail?.generations?.filter(
                      (item) => item.videoId !== generationId,
                    ),
                  })
                  setCurrentSelectedGenerationIndex(
                    newCurrentSelectedGenerationIndex ?? 0,
                  )
                  updateHistoryVideoId(showVideoDetail.videoId)
                  return
                }

                // 如果prev和next不存在，则关闭modal
                videoDetailModal.close()
              }}
            />
          </DetailPrimitive.Right>
        </DetailPrimitive.Container>

        {isModal && actions && (
          <div
            className={cls`absolute end-full top-1/2 hidden -translate-y-1/2 flex-col gap-y-3 sm:flex ltr:-translate-x-3 rtl:translate-x-3`}
          >
            <Button
              type='primary'
              className='bg-f-bg-container'
              disabled={!isHasPrev}
              icon={<CssIcon className={cls`i-com--up size-6`} />}
              onClick={() => {
                const prev: TVideoDetailVo | undefined = actions.prevRecord(
                  videoDetail.videoId,
                )
                if (prev) {
                  updateHistoryVideoId(prev.videoId)
                  setCurrentSelectedGenerationIndex(
                    prev.currentSelectedGenerationIndex ?? 0,
                  )
                  setVideoDetail(prev)
                }
              }}
            />
            <Button
              type='primary'
              className='bg-f-bg-container'
              disabled={!isHasNext}
              icon={<CssIcon className={cls`i-com--down size-6`} />}
              onClick={() => {
                const next: TVideoDetailVo | undefined = actions.nextRecord(
                  videoDetail.videoId,
                )
                if (next) {
                  updateHistoryVideoId(next.videoId)
                  setCurrentSelectedGenerationIndex(
                    next.currentSelectedGenerationIndex ?? 0,
                  )
                  setVideoDetail(next)
                }
              }}
            />
          </div>
        )}
      </DetailPrimitive.Root>
    </InspirationModalContextProvider>
  )
}

export default Detail

import TrackElement from '@/_blocks/Analytics/blocks/TrackElement'
import { useContentTrack } from '@/_blocks/Analytics/hooks/useContentTrack'
import { useScreenIdle } from '@/hooks/screen'
import IconWithTooltip from '@/pages/pollo.ai/_components/MenuOptions/_components/IconWithTooltip'
import { useCreateSimilarVideo } from '@/pages/pollo.ai/_hooks/useCreateSimilarVideo'
import {
  TabType,
  useFormHistoryTabStore,
} from '@/pages/pollo.ai/app/_store/formHistoryTab'
import { useLingui } from '@lingui/react/macro'
import { Button, type ButtonProps } from 'antd'
import { pick } from 'lodash-es'
import React from 'react'

import type { IIconWithTooltipProps } from '@/pages/pollo.ai/_components/MenuOptions/_components/IconWithTooltip'
import type { CreationItem } from '@/pages/pollo.ai/_types/video'

import { useIsProtected } from '../../../../_hooks/useProtected'

interface ICreateSimilarVideoProps
  extends ButtonProps,
    Pick<IIconWithTooltipProps, 'showTooltip' | 'tooltipProps'> {
  data: CreationItem
  text: React.ReactNode
  protectedText?: React.ReactNode
  openInNewWindow?: boolean
}
const CreateSimilarVideo = (props: ICreateSimilarVideoProps) => {
  const {
    data,
    showTooltip,
    tooltipProps,
    text,
    protectedText,
    openInNewWindow = false,
    ...restProps
  } = props

  const { t } = useLingui()

  const { matched } = useScreenIdle('md')
  const { isProtected } = useIsProtected(data)
  const { createSimilarVideo, createSameTool } = useCreateSimilarVideo()
  const { setActiveTab } = useFormHistoryTabStore()

  const { getClickEventParams } = useContentTrack()

  const tooltipTitle = useMemo(() => {
    if (isProtected && matched) return t`Copyright protection enabled`
    return tooltipProps?.title
  }, [isProtected, tooltipProps?.title, t, matched])

  return (
    <IconWithTooltip
      showTooltip={showTooltip || (isProtected && matched)}
      tooltipProps={{
        ...tooltipProps,
        title: tooltipTitle,
      }}
    >
      <TrackElement clickEvent={getClickEventParams(data, 'create_similar')}>
        <Button
          className={cls`ms-auto flex h-7 items-center justify-center gap-x-1 px-3 text-sm font-semibold md:px-3`}
          type='primary'
          {...restProps}
          onClick={() => {
            setActiveTab(TabType.Create)
            if (isProtected) {
              createSameTool(
                pick(data, ['generateRecord', 'mediaType', 'user', 'videoId']),
                openInNewWindow,
              )
            } else {
              createSimilarVideo(
                {
                  videoId: data?.videoId,
                  generateRecord: data?.generateRecord,
                  mediaType: data?.mediaType,
                  user: data?.user,
                },
                openInNewWindow,
              )
            }
          }}
        >
          <span>
            {isProtected ? protectedText || t`Try the Same Tool` : text}
          </span>
        </Button>
      </TrackElement>
    </IconWithTooltip>
  )
}

export default CreateSimilarVideo

import { useContentTrack } from '@/_blocks/Analytics/hooks/useContentTrack'
import { useLocale } from '@/hooks/routes'
import { useAgentModeABTest } from '@/hooks/userGroup/abTest/agentModeABTest'
import MenuOptions from '@/pages/pollo.ai/_components/MenuOptions'
import { exploreSourceList } from '@/pages/pollo.ai/explore/_constant'
import { useInspirationModalContext } from '@/pages/pollo.ai/home/canvas/_entities/tools/generations/inspirations/store/context'
import { Trans, useLingui } from '@lingui/react/macro'
import { EProcessVideoType, EVideoStatus } from '@loc/server/enums/video'
import { EntryCodeType } from '@loc/server/enums/video.common'
import { useLatest } from 'ahooks'
import { match, P } from 'ts-pattern'

import type {
  MenuCustomOption,
  TImageMenuOptionKey,
  TVideoMenuOptionKey,
} from '@/pages/pollo.ai/_components/MenuOptions/_types'
import type { CreationItem } from '@/pages/pollo.ai/_types/video'

import useVisibleImageOptions from '../../../_hooks/useVisibleImageOptions'
import Footer from '../Footer'
import { useDetailContext } from './context'

export function RightBottom({
  onUpdateVideo,
  updateFavorite,
  onDeleteImageSuccess,
  openInNewWindow,
}: {
  onDeleteImageSuccess: (generationId: string) => void
  onUpdateVideo: ({
    isRefetch,
    videoData,
  }: {
    isRefetch?: boolean
    videoData?: CreationItem
  }) => void
  updateFavorite: (videoId: string, favorite: boolean) => void
  openInNewWindow?: boolean
}) {
  const {
    videoDetail,
    isImage,
    isMySelf,
    currentSelectedGenerationIndex,
    menuOptionsData,
  } = useDetailContext()

  const [isFromExplore, setIsFromExplore] = useState(false)

  const { isAgentMode } = useAgentModeABTest()

  useInsertionEffect(() => {
    const urlParams = new URLSearchParams(
      typeof window !== 'undefined' ? window.location.search : '',
    )
    const from = urlParams.get('from')

    const isFromExplore =
      exploreSourceList.findIndex((i) => from?.startsWith(i)) > -1

    setIsFromExplore(!!isFromExplore)
  }, [])

  const { t } = useLingui()
  const { useCurrentUrlJump } = useInspirationModalContext()
  const { handleClick } = useContentTrack()
  const videoDetailRef = useLatest<CreationItem>(videoDetail)

  const selectedGeneration = useMemo(() => {
    return videoDetail?.generations?.[currentSelectedGenerationIndex]
  }, [currentSelectedGenerationIndex, videoDetail])

  const { imageOptions } = useVisibleImageOptions(videoDetail)

  const { withdrawLoras } = useMemo(() => {
    const generationLoras = videoDetail.generateRecord.generationLoras ?? []
    const withdrawLoras = generationLoras.filter((lora) => lora.status === 0)
    return {
      withdrawLoras,
    }
  }, [videoDetail])

  const hideAiAvatar = [
    EntryCodeType.LipSyncWorkflowTemplate,
    EntryCodeType.AiAvatar,
    EntryCodeType.EffectsAndTemplates,
  ].includes(videoDetail.generateRecord.entryCode || '')

  const isAgentDetail =
    (menuOptionsData.generateRecord.entryCode ??
      videoDetail.generateRecord.entryCode) === 'Agent'

  const imageMenuOptions = useMemo(() => {
    const hasWithdrawLora = withdrawLoras.length > 0
    const hasWithdrawLoraTitle = withdrawLoras
      .map((lora) => lora.title)
      .join(', ')

    return match(menuOptionsData)
      .returnType<MenuCustomOption<TImageMenuOptionKey>[]>()
      .with(
        {
          generateRecord: {
            processType: P.union(
              EProcessVideoType.IMAGE_BACKGROUND_REMOVER,
              EProcessVideoType.IMAGE_OBJECT_REMOVER,
              EProcessVideoType.IMAGE_UPSCALE,
              EProcessVideoType.IMAGE_FACE_SWAP,
              EProcessVideoType.IMAGE_PAINTING,
            ),
          },
        },
        () => [
          {
            key: 're-edit',
          },
        ],
      )
      .with(
        {
          generateRecord: {
            processType: P.union(
              EProcessVideoType.IMAGE_BACKGROUND_CHANGER,
              EProcessVideoType.IMAGE_UNCROP,
            ),
          },
        },
        () => [
          {
            key: 're-edit',
          },
          {
            key: 'reGenerate',
          },
        ],
      )
      .otherwise(() => [
        {
          key: 'reprompt',
          disabled: hasWithdrawLora,
          showTooltip: hasWithdrawLora,

          tooltipProps: {
            title: hasWithdrawLora
              ? t`[${hasWithdrawLoraTitle}] has been removed. Please try a different LoRA.`
              : t`Reprompt`,
          },
        },
        {
          key: 'reGenerate',
          disabled: hasWithdrawLora,
          showTooltip: hasWithdrawLora,

          tooltipProps: {
            title: hasWithdrawLora
              ? t`[${hasWithdrawLoraTitle}] has been removed. Please try a different LoRA.`
              : t`Regenerate`,
          },
        },
      ])
      .map((i) => ({ show: true, ...i }))
      .filter((item) => item.show)
  }, [withdrawLoras, menuOptionsData, t])

  const visibleImageOptions = useMemo(() => {
    const processType = menuOptionsData.generateRecord?.processType
    // Keep consistent with the original MenuOptions logic: `re-edit` and `reprompt` are mutually exclusive.
    // `re-edit` is currently hidden (see below), but we still use this flag to avoid showing `reprompt`
    // for records that only support `re-edit`.
    const canReEdit =
      menuOptionsData.generateRecord.entryCode === EntryCodeType.ImageTool ||
      [
        EProcessVideoType.IMAGE_BACKGROUND_REMOVER,
        EProcessVideoType.IMAGE_OBJECT_REMOVER,
        EProcessVideoType.IMAGE_UNCROP,
        EProcessVideoType.IMAGE_UPSCALE,
        EProcessVideoType.IMAGE_PAINTING,
        EProcessVideoType.IMAGE_BACKGROUND_CHANGER,
      ].includes(processType as EProcessVideoType)

    const repromptConfig = imageMenuOptions.find((i) => i.key === 'reprompt')
    const {
      show: _repromptShow,
      key: _repromptKey,
      ...repromptConfigRest
    } = (repromptConfig ?? {}) as MenuCustomOption<TImageMenuOptionKey> & {
      show?: boolean
    }
    const imageOptionConfigMap = new Map(
      imageOptions.map((item) => [item.key, item] as const),
    )

    const list: MenuCustomOption<TImageMenuOptionKey>[] = Array.from<
      MenuCustomOption<TImageMenuOptionKey> & { show?: boolean }
    >([
      {
        key: 'addToAgent',
        show:
          !isAgentDetail &&
          isAgentMode &&
          !!(
            menuOptionsData.videoUrl ||
            menuOptionsData.mediaUrl ||
            menuOptionsData.reallyVideoUrl ||
            menuOptionsData.reallyMediaUrl ||
            menuOptionsData.temp?.imageUrl ||
            menuOptionsData.temp?.image ||
            menuOptionsData.temp?.images?.[0]
          ),
      },
      {
        key: 'imageToVideo',
      },
      {
        key: 'imageToImage',
      },
      {
        key: 'imageToShorts',
      },
      {
        key: 'aiAvatar',
      },
      {
        key: 're-edit',
        show: isMySelf && canReEdit && !isAgentDetail,
      },
      {
        key: 'reprompt',
        ...repromptConfigRest,
        show: isMySelf && !canReEdit && !isAgentDetail,
      },
      {
        key: 'inpaint',
        ...imageOptionConfigMap.get('inpaint'),
        show: isMySelf,
      },
      {
        key: 'bgRemove',
        ...imageOptionConfigMap.get('bgRemove'),
        show: isMySelf,
      },
      {
        key: 'objectRemover',
        ...imageOptionConfigMap.get('objectRemover'),
        show: isMySelf,
      },
      {
        key: 'uncrop',
        ...imageOptionConfigMap.get('uncrop'),
        show: isMySelf,
      },
      {
        key: 'aiBackground',
        ...imageOptionConfigMap.get('aiBackground'),
        show: isMySelf,
      },
    ])
      .map((i) => ({
        show: true,
        sourceType: 'detailModal' as const,
        onTrack: (element: HTMLElement) => {
          i.key && handleClick(videoDetailRef.current, element, i.key)
        },
        ...i,
      }))
      .filter((item) => item.show)

    return list
  }, [
    handleClick,
    imageMenuOptions,
    imageOptions,
    isMySelf,
    isAgentDetail,
    menuOptionsData.generateRecord.entryCode,
    menuOptionsData.generateRecord?.processType,
    videoDetailRef,
    isAgentMode,
  ])

  const isProcessing =
    videoDetail.generateRecord.status === EVideoStatus.Processing
  const isSucceed = videoDetail.generateRecord.status === EVideoStatus.Succeed

  const visibleVideoOptions = useMemo(() => {
    const list: MenuCustomOption<TVideoMenuOptionKey>[] = Array.from<
      MenuCustomOption<TVideoMenuOptionKey> & { show: boolean }
    >([
      {
        key: 'videoToVideo',
        show: isMySelf && isSucceed,
        sourceType: 'detailModal',
      },
      {
        key: 'lipSync',
        show: isMySelf && isSucceed && !hideAiAvatar,
        sourceType: 'detailModal',
      },
      {
        key: 'reGenerate',
        show: isMySelf && !isProcessing,
        sourceType: 'detailModal',
      },
      {
        key: 'reprompt',
        show: isMySelf && !isProcessing,
        sourceType: 'detailModal',
      },
      {
        key: 'videoEdit',
        show: isMySelf && isSucceed,
        sourceType: 'detailModal',
      },
      {
        key: 'upscale',
        show: isMySelf && isSucceed,
        sourceType: 'detailModal',
      },
      {
        key: 'videoExtender',
        show: isMySelf && isSucceed,
        sourceType: 'detailModal',
      },
      {
        key: 'addSounds',
        show: isMySelf && isSucceed,
        sourceType: 'detailModal',
      },
    ]).filter((item) => item.show)
    return list
  }, [hideAiAvatar, isMySelf, isProcessing, isSucceed])

  const hasVideoCreateOptions = useMemo(() => {
    return visibleVideoOptions.some(
      (item) => item.key === 'videoToVideo' || item.key === 'lipSync',
    )
  }, [visibleVideoOptions])

  const hasVideoEditOptions = useMemo(() => {
    const editKeys: TVideoMenuOptionKey[] = isAgentDetail
      ? ['videoEdit', 'upscale', 'videoExtender', 'addSounds']
      : [
          'reGenerate',
          'reprompt',
          'videoEdit',
          'upscale',
          'videoExtender',
          'addSounds',
        ]

    return visibleVideoOptions.some((item) => editKeys.includes(item.key))
  }, [isAgentDetail, visibleVideoOptions])

  const locale = useLocale()

  const colsTwoLocales = ['ar', 'ru']

  return (
    <div className='flex flex-col gap-y-5'>
      {isImage ? (
        <div className='flex flex-col gap-3'>
          <div className='flex flex-col gap-2'>
            <p className='text-f-text-secondary flex items-center gap-0.5 text-xs'>
              <Trans>Create</Trans>
            </p>

            <MenuOptions
              openInNewWindow={openInNewWindow}
              onlyExpand
              data={menuOptionsData}
              visibleOptions={
                isFromExplore
                  ? visibleImageOptions.filter((item) =>
                      [
                        'imageToVideo',
                        'imageToImage',
                        'imageToShorts',
                        'aiAvatar',
                      ].includes(item.key),
                    )
                  : visibleImageOptions
              }
              expand={
                !isAgentDetail
                  ? ([
                      'addToAgent',
                      'imageToVideo',
                      'imageToImage',
                      'imageToShorts',
                      'aiAvatar',
                    ] as const)
                  : ([
                      'imageToVideo',
                      'imageToImage',
                      'imageToShorts',
                      'aiAvatar',
                    ] as const)
              }
              menuType={'image'}
              className={cls`grid grid-cols-2 gap-2 ${colsTwoLocales.includes(locale) ? '' : 'md:grid-cols-3'}`}
              optionClassName={cls`hover:!bg-f-bg-hover h-8 justify-start text-xs`}
            />
          </div>
          {visibleImageOptions.some((item) =>
            [
              ...(isAgentDetail
                ? ([] as const)
                : (['re-edit', 'reprompt'] as const)),
              'inpaint',
              'bgRemove',
              'objectRemover',
              'uncrop',
              'aiBackground',
            ].includes(item.key),
          ) &&
            !isFromExplore &&
            !useCurrentUrlJump &&
            isMySelf && (
              <>
                <p className='text-f-text-secondary flex items-center gap-0.5 text-xs'>
                  <Trans>Edit</Trans>
                </p>
                <MenuOptions
                  onlyExpand
                  data={menuOptionsData}
                  visibleOptions={visibleImageOptions}
                  expand={
                    isAgentDetail
                      ? ([
                          'inpaint',
                          'bgRemove',
                          'objectRemover',
                          'uncrop',
                          'aiBackground',
                        ] as const)
                      : ([
                          're-edit',
                          'reprompt',
                          'inpaint',
                          'bgRemove',
                          'objectRemover',
                          'uncrop',
                          'aiBackground',
                        ] as const)
                  }
                  menuType={'image'}
                  className={cls`grid grid-cols-2 gap-2 ${colsTwoLocales.includes(locale) ? '' : 'md:grid-cols-3'}`}
                  optionClassName={cls`hover:!bg-f-bg-hover h-8 justify-start text-xs`}
                />
              </>
            )}
        </div>
      ) : (
        !useCurrentUrlJump &&
        (hasVideoCreateOptions || (!isFromExplore && hasVideoEditOptions)) && (
          <div className='flex flex-col gap-2'>
            <div className='flex flex-col gap-2'>
              {hasVideoCreateOptions && (
                <>
                  <p className='text-f-text-secondary flex items-center gap-1.5 text-xs'>
                    <Trans>Create</Trans>
                  </p>
                  <MenuOptions
                    onlyExpand
                    data={videoDetail}
                    visibleOptions={visibleVideoOptions}
                    generation={selectedGeneration}
                    expand={['videoToVideo', 'lipSync']}
                    menuType={'video'}
                    className={cls`grid grid-cols-2 gap-2`}
                    optionClassName={cls`hover:!bg-f-bg-hover h-8 justify-start text-xs`}
                  />
                </>
              )}

              {!isFromExplore && hasVideoEditOptions && (
                <>
                  <p className='text-f-text-secondary flex items-center gap-1.5 text-xs'>
                    <Trans>Edit</Trans>
                  </p>
                  <MenuOptions
                    onlyExpand
                    data={videoDetail}
                    visibleOptions={visibleVideoOptions}
                    generation={selectedGeneration}
                    expand={
                      isAgentDetail
                        ? ([
                            'videoEdit',
                            'upscale',
                            'videoExtender',
                            'addSounds',
                          ] as const)
                        : ([
                            'reGenerate',
                            'reprompt',
                            'videoEdit',
                            'upscale',
                            'videoExtender',
                            'addSounds',
                          ] as const)
                    }
                    menuType={'video'}
                    className={cls`grid grid-cols-2 gap-2`}
                    optionClassName={cls`hover:!bg-f-bg-hover h-8 justify-start text-xs`}
                  />
                </>
              )}
            </div>
          </div>
        )
      )}

      {isImage ? (
        <Footer
          data={menuOptionsData}
          isImage
          onUpdateVideo={onUpdateVideo}
          updateFavorite={updateFavorite}
          generation={selectedGeneration}
          onDeleteImageSuccess={onDeleteImageSuccess}
          openInNewWindow={openInNewWindow}
        />
      ) : (
        <Footer
          data={videoDetail}
          onUpdateVideo={onUpdateVideo}
          updateFavorite={updateFavorite}
          generation={selectedGeneration}
          openInNewWindow={openInNewWindow}
        />
      )}
    </div>
  )
}

import { useContentTrack } from '@/_blocks/Analytics/hooks/useContentTrack'
import {
  getIsRecordFromImageTool,
  getIsRecordFromVideoTools,
} from '@/pages/pollo.ai/_components/CreationRecords/helper'
import { useVideoPublishStatus } from '@/pages/pollo.ai/_components/MenuOptions/_hooks/menuStates/publish/videoPublishStatus'
import { useExtendHidden } from '@/pages/pollo.ai/_hooks/useExtendDIsable'
import { VideoLike } from '@/pages/pollo.ai/assets/_blocks/VideoWindow/VideoLike'
import { useIsLipSyncVideoType } from '@/pages/pollo.ai/tool/_blocks/tools/VideoLipSync/_hooks/useIsLipSyncVideoType'
import { useIsMySelf } from '@/pages/pollo.ai/v/[id]/_hooks/isMySelf'
import { useLingui } from '@lingui/react/macro'
import { EProcessVideoType } from '@loc/server/enums/video'

import type {
  MenuCoreOption,
  MenuCustomOption,
  TImageMenuOptionKey,
  TVideoMenuOptionKey,
} from '@/pages/pollo.ai/_components/MenuOptions/_types'
import type {
  CreationItem,
  GenerationItem,
} from '@/pages/pollo.ai/_types/video'

import CreateSimilarVideo from './CreateSimilarVideo'
import FooterMenuOptions from './FooterMenuOptions'

import type { TOnUpdateVideo } from '../../../_hooks/useVideoDetail'

interface IFooterProps {
  data: CreationItem & {
    reallyVideoId?: string
    reallyVideoUrl?: string
  }
  generation?: GenerationItem
  onUpdateVideo?: TOnUpdateVideo
  isImage?: boolean
  onDeleteImageSuccess?: (generationId: string) => void
  updateFavorite?: (videoId: string, favorite: boolean) => void
  openInNewWindow?: boolean
}

const Footer = (props: IFooterProps) => {
  const {
    data,
    onUpdateVideo,
    isImage,
    onDeleteImageSuccess,
    updateFavorite,
    generation,
    openInNewWindow,
  } = props

  const { t } = useLingui()

  const { isMySelf } = useIsMySelf(data)
  const isRecordGenerateByVideoSound =
    data.generateRecord.processType === EProcessVideoType.VIDEO2AUDIOVIDEO
  const { handleClick } = useContentTrack()

  const { isSimilarDisable } = useExtendHidden({ video: data })

  // FIXME: 兼容一下
  const { isPublished } = useVideoPublishStatus({
    videoItem: generation ? { ...data, ...generation } : data,
  })

  const isAgentDetail = data.generateRecord.entryCode === 'Agent'

  const generateRecordType = data?.generateRecord?.type
  const isLipSync = useIsLipSyncVideoType({ videoType: generateRecordType })
  const isFromVideoTools = getIsRecordFromVideoTools(data)

  const visibleVideoOptions = useMemo(() => {
    const list: MenuCustomOption<TVideoMenuOptionKey>[] = Array.from<
      MenuCustomOption<TVideoMenuOptionKey> & {
        show: boolean
        children?: MenuCoreOption[]
      }
    >([
      {
        key: 'copyLink',
        sourceType: 'detailModal',
        show: true,
        showTooltip: true,
        onTrack: (element) => {
          handleClick(data, element, 'copy_link')
        },
      },
      {
        key: 'share',
        sourceType: 'detailModal',
        show: true,
        children: [
          {
            key: 'x',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'share_to_x')
            },
          },
          {
            key: 'facebook',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'share_to_facebook')
            },
          },
        ] as MenuCoreOption[],
      },
      {
        key: 'publish',
        sourceType: 'detailModal',
        show: isMySelf && !isLipSync,
        disabled: isAgentDetail,
        onSuccess: async (data) => {
          if (data) {
            onUpdateVideo?.({ videoData: data })
          }
        },
        onTrack: (element) => {
          handleClick(data, element, 'publish')
        },
      },
      {
        key: 'copyProtection',
        sourceType: 'detailModal',
        show: isMySelf && isPublished,
        onSuccess: async (data) => {
          if (data) {
            onUpdateVideo?.({ videoData: data })
          }
        },
        showTooltip: true,
        onTrack: (element) => {
          handleClick(data, element, 'copy_protection')
        },
      },
      {
        key: 'favorite',
        sourceType: 'detailModal',
        show: isMySelf,
        onSuccess: async (data) => {
          updateFavorite?.(data!.videoId, data!.favorite)
        },
        onTrack: (element) => {
          handleClick(data, element, 'add_to_favorite')
        },
      },
      {
        key: 'download',
        sourceType: 'detailModal',
        show: true,
        children: [
          {
            key: 'with-watermark',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'download_with_watermark')
            },
          },
          {
            key: 'without-watermark',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'download_without_watermark')
            },
          },
        ] as MenuCoreOption[],
      },
      {
        key: 'report',
        sourceType: 'detailModal',
        show: true,
        onTrack: (element) => {
          handleClick(data, element, 'report')
        },
      },
      {
        key: 'delete',
        sourceType: 'detailModal',
        show: isMySelf,
        onTrack: (element) => {
          handleClick(data, element, 'delete')
        },
      },
    ]).filter((item) => item.show)

    return list as MenuCustomOption<TVideoMenuOptionKey>[]
  }, [
    data,
    handleClick,
    isAgentDetail,
    isLipSync,
    isMySelf,
    isPublished,
    onUpdateVideo,
    updateFavorite,
  ])

  const visibleImageOptions = useMemo(() => {
    const list: MenuCustomOption<TImageMenuOptionKey>[] = Array.from<
      MenuCustomOption<TImageMenuOptionKey> & {
        show: boolean
        children?: MenuCoreOption[]
      }
    >([
      {
        key: 'publish',
        sourceType: 'detailModal',
        show: isMySelf && !isLipSync,
        disabled: isAgentDetail,
        onSuccess: async (data) => {
          if (data) {
            onUpdateVideo?.({ videoData: data })
          }
        },
        onTrack: (element) => {
          handleClick(data, element, 'publish')
        },
      },
      {
        key: 'copyLink',
        sourceType: 'detailModal',
        show: true,
        showTooltip: true,
        onTrack: (element) => {
          handleClick(data, element, 'copy_link')
        },
      },
      {
        key: 'copyProtection',
        sourceType: 'detailModal',
        show: isMySelf && isPublished,
        onSuccess: async (data) => {
          if (data) {
            onUpdateVideo?.({ videoData: data })
          }
        },
        showTooltip: true,
        onTrack: (element) => {
          handleClick(data, element, 'copy_protection')
        },
      },
      {
        key: 'share',
        sourceType: 'detailModal',
        show: true,
        children: [
          {
            key: 'x',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'share_to_x')
            },
          },
          {
            key: 'facebook',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'share_to_facebook')
            },
          },
        ] as MenuCoreOption[],
      },
      {
        key: 'favorite',
        sourceType: 'detailModal',
        show: isMySelf,
        onSuccess: async (data) => {
          updateFavorite?.(data!.videoId, data!.favorite)
        },
        onTrack: (element) => {
          handleClick(data, element, 'add_to_favorite')
        },
      },
      {
        key: 'download',
        sourceType: 'detailModal',
        show: true,
        children: [
          {
            key: 'with-watermark',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'download_with_watermark')
            },
          },
          {
            key: 'without-watermark',
            onTrack: (element: HTMLElement) => {
              handleClick(data, element, 'download_without_watermark')
            },
          },
        ] as MenuCoreOption[],
      },
      {
        key: 'report',
        sourceType: 'detailModal',
        show: true,
        onTrack: (element) => {
          handleClick(data, element, 'report')
        },
      },
      {
        key: 'delete',
        sourceType: 'detailModal',
        show: isMySelf,
        onSuccess: async () => {
          onDeleteImageSuccess?.(data.videoId)
        },
        onTrack: (element) => {
          handleClick(data, element, 'delete')
        },
      },
    ]).filter((item) => item.show)

    return list as MenuCustomOption<TImageMenuOptionKey>[]
  }, [
    isMySelf,
    isLipSync,
    isPublished,
    isAgentDetail,
    onUpdateVideo,
    handleClick,
    data,
    updateFavorite,
    onDeleteImageSuccess,
  ])

  const isRecordFromImageTool = getIsRecordFromImageTool(data)

  return (
    <div className={cls`flex w-full items-center gap-2`}>
      <VideoLike
        className={cls`bg-f-bg-layout hover:bg-f-bg-hover text-f-text-tertiary hover:text-f-primary group`}
        iconClassName={cls`group-hover:text-f-primary`}
        videoId={data.videoId}
        isLeft={false}
        isLike={data?.isLike || false}
        likeNum={data.starNum}
        onChange={({ isLike, starNum }) => {
          onUpdateVideo?.({
            videoData: {
              ...data,
              isLike,
              starNum,
            },
          })
        }}
        onTrack={(element: HTMLElement) => {
          handleClick(data, element, 'like')
        }}
      />
      {isImage ? (
        <FooterMenuOptions
          data={data}
          visibleOptions={visibleImageOptions}
          menuType={'image'}
          generation={generation}
        />
      ) : (
        <FooterMenuOptions
          data={data}
          visibleOptions={visibleVideoOptions}
          menuType={'video'}
          generation={generation}
        />
      )}

      {!isAgentDetail && (
        <>
          {isImage
            ? !isRecordFromImageTool && (
                <CreateSimilarVideo
                  data={data}
                  text={t`Create Similar Image`}
                  openInNewWindow={openInNewWindow}
                />
              )
            : !isRecordGenerateByVideoSound &&
              !isFromVideoTools && (
                <CreateSimilarVideo
                  data={data}
                  showTooltip={isSimilarDisable}
                  tooltipProps={{
                    title: isSimilarDisable
                      ? t`You cannot create a similar video from an extended video.`
                      : '',
                    arrow: false,
                  }}
                  disabled={isSimilarDisable}
                  text={t`Create Similar Video`}
                  openInNewWindow={openInNewWindow}
                />
              )}
        </>
      )}
    </div>
  )
}

export default Footer

import MenuOptions from '@/pages/pollo.ai/_components/MenuOptions'

import type { IMenuCoreOptionsProps } from '@/pages/pollo.ai/_components/MenuOptions'
import type {
  MenuCustomOption,
  TImageMenuOptionKey,
  TVideoMenuOptionKey,
} from '@/pages/pollo.ai/_components/MenuOptions/_types'
import type { GenerationItem } from '@/pages/pollo.ai/_types/video'

interface FooterMenuOptionsBaseProps {
  data: IMenuCoreOptionsProps['data']
  generation?: GenerationItem
  iconClassName?: string
}

type FooterMenuOptionsProps =
  | (FooterMenuOptionsBaseProps & {
      menuType: 'video'
      visibleOptions: MenuCustomOption<TVideoMenuOptionKey>[]
    })
  | (FooterMenuOptionsBaseProps & {
      menuType: 'image'
      visibleOptions: MenuCustomOption<TImageMenuOptionKey>[]
    })

const FILTER_OUT_KEYS = new Set(['download', 'share'])

const VIDEO_EXPAND_KEYS = [
  'publish',
  'copyLink',
  'favorite',
] satisfies TVideoMenuOptionKey[]

const IMAGE_EXPAND_KEYS = [
  'publish',
  'copyLink',
  'favorite',
] satisfies TImageMenuOptionKey[]

const FooterMenuOptions = (props: FooterMenuOptionsProps) => {
  const {
    data,
    generation,
    iconClassName = '!size-[18px]',
    menuType,
    visibleOptions: _visibleOptions,
  } = props

  if (menuType === 'video') {
    const visibleOptions = _visibleOptions.filter(
      (item) => !FILTER_OUT_KEYS.has(item.key),
    )

    return (
      <MenuOptions
        data={data}
        iconClassName={iconClassName}
        visibleOptions={visibleOptions}
        expand={VIDEO_EXPAND_KEYS}
        menuType='video'
        iconOnly
        generation={generation}
      />
    )
  }

  const visibleOptions = _visibleOptions.filter(
    (item) => !FILTER_OUT_KEYS.has(item.key),
  )

  return (
    <MenuOptions
      data={data}
      iconClassName={iconClassName}
      visibleOptions={visibleOptions}
      expand={IMAGE_EXPAND_KEYS}
      menuType='image'
      iconOnly
      generation={generation}
    />
  )
}

export default FooterMenuOptions

import TrackElement from '@/_blocks/Analytics/blocks/TrackElement'
import { useContentTrack } from '@/_blocks/Analytics/hooks/useContentTrack'
import { useScreenIdle } from '@/hooks/screen'
import IconWithTooltip from '@/pages/pollo.ai/_components/MenuOptions/_components/IconWithTooltip'
import { useCreateSimilarVideo } from '@/pages/pollo.ai/_hooks/useCreateSimilarVideo'
import {
  TabType,
  useFormHistoryTabStore,
} from '@/pages/pollo.ai/app/_store/formHistoryTab'
import { useLingui } from '@lingui/react/macro'
import { Button, type ButtonProps } from 'antd'
import { pick } from 'lodash-es'
import React from 'react'

import type { IIconWithTooltipProps } from '@/pages/pollo.ai/_components/MenuOptions/_components/IconWithTooltip'
import type { CreationItem } from '@/pages/pollo.ai/_types/video'

import { useIsProtected } from '../../../../_hooks/useProtected'

interface ICreateSimilarVideoProps
  extends ButtonProps,
    Pick<IIconWithTooltipProps, 'showTooltip' | 'tooltipProps'> {
  data: CreationItem
  text: React.ReactNode
  protectedText?: React.ReactNode
  openInNewWindow?: boolean
}
const CreateSimilarVideo = (props: ICreateSimilarVideoProps) => {
  const {
    data,
    showTooltip,
    tooltipProps,
    text,
    protectedText,
    openInNewWindow = false,
    ...restProps
  } = props

  const { t } = useLingui()

  const { matched } = useScreenIdle('md')
  const { isProtected } = useIsProtected(data)
  const { createSimilarVideo, createSameTool } = useCreateSimilarVideo()
  const { setActiveTab } = useFormHistoryTabStore()

  const { getClickEventParams } = useContentTrack()

  const tooltipTitle = useMemo(() => {
    if (isProtected && matched) return t`Copyright protection enabled`
    return tooltipProps?.title
  }, [isProtected, tooltipProps?.title, t, matched])

  return (
    <IconWithTooltip
      showTooltip={showTooltip || (isProtected && matched)}
      tooltipProps={{
        ...tooltipProps,
        title: tooltipTitle,
      }}
    >
      <TrackElement clickEvent={getClickEventParams(data, 'create_similar')}>
        <Button
          className={cls`ms-auto flex h-7 items-center justify-center gap-x-1 px-3 text-sm font-semibold md:px-3`}
          type='primary'
          {...restProps}
          onClick={() => {
            setActiveTab(TabType.Create)
            if (isProtected) {
              createSameTool(
                pick(data, ['generateRecord', 'mediaType', 'user', 'videoId']),
                openInNewWindow,
              )
            } else {
              createSimilarVideo(
                {
                  videoId: data?.videoId,
                  generateRecord: data?.generateRecord,
                  mediaType: data?.mediaType,
                  user: data?.user,
                },
                openInNewWindow,
              )
            }
          }}
        >
          <span>
            {isProtected ? protectedText || t`Try the Same Tool` : text}
          </span>
        </Button>
      </TrackElement>
    </IconWithTooltip>
  )
}

export default CreateSimilarVideo

