import { Trans } from '@lingui/react/macro'
import { useHover } from 'ahooks'
import { Button, type ButtonProps } from 'antd'

import { useButtonState } from '../../_hooks/useButtonState'
import EllipsisText from '../EllipsisText'
import IconWithTooltip from '../IconWithTooltip'
import UpgradeIcon from '../UpgradeIcon'

import type { MenuOption } from '../../_types'

const OptionButton = (
  props: Omit<ButtonProps, 'children' | 'onClick'> & MenuOption,
) => {
  const {
    className,
    premium,
    iconOnly,
    icon: _icon,
    label: _label,
    onClick,
    onTrack,
    tooltipProps,
    showTooltip,
    comingSoon,
    ...restProps
  } = props
  const ref = useRef<HTMLButtonElement>(null)
  const isHovering = useHover(ref)

  const { icon, label, tooltipTitle, setState } = useButtonState({
    icon: _icon,
    label: _label,
    tooltipTitle: tooltipProps?.title,
  })
  const [isLabelEllipsis, setIsLabelEllipsis] = useState(false)
  const displayLabel =
    comingSoon && isHovering ? <Trans>Coming Soon</Trans> : label
  const shouldShowOverflowTooltip = !showTooltip && !iconOnly && isLabelEllipsis
  const buttonTooltipTitle = showTooltip ? tooltipTitle : displayLabel
  const shouldShowButtonTooltip =
    (showTooltip && !!tooltipTitle) || shouldShowOverflowTooltip

  return (
    <IconWithTooltip
      tooltipProps={{ ...tooltipProps, title: buttonTooltipTitle }}
      showTooltip={shouldShowButtonTooltip}
    >
      <Button
        ref={ref}
        className={tw`!bg-f-bg-layout text-f-text-tertiary flex h-7 items-center gap-x-1 !border-none !px-2 text-sm ${className}`}
        icon={
          comingSoon && isHovering ? null : (
            <CssIcon className={cls`${icon} size-4`} />
          )
        }
        onClick={(e) => {
          onTrack?.(e.currentTarget)
          onClick?.({ ...setState, domEvent: e })
        }}
        {...restProps}
      >
        <div className={cls`flex w-full min-w-0 items-center gap-x-1`}>
          {!iconOnly && (
            <EllipsisText
              className={cls`!mb-0 min-w-0 flex-1`}
              onEllipsis={setIsLabelEllipsis}
            >
              <>{displayLabel}</>
            </EllipsisText>
          )}
          {premium && <UpgradeIcon />}
        </div>
      </Button>
    </IconWithTooltip>
  )
}

export default OptionButton

import { useMemoizedFn } from 'ahooks'

interface EllipsisTextProps {
  className?: string
  children: React.ReactNode
  onEllipsis?: (isEllipsis: boolean) => void
}

const EllipsisText = ({
  className,
  children,
  onEllipsis,
}: EllipsisTextProps) => {
  const textRef = useRef<HTMLSpanElement>(null)

  const checkEllipsis = useMemoizedFn(() => {
    const element = textRef.current
    if (!element) return

    onEllipsis?.(element.scrollWidth > element.clientWidth)
  })

  useEffect(() => {
    checkEllipsis()
  }, [children, checkEllipsis])

  return (
    <span
      ref={textRef}
      className={cls`block min-w-0 flex-1 truncate text-left ${className}`}
      onMouseEnter={checkEllipsis}
    >
      {children}
    </span>
  )
}

export default EllipsisText

