import { useMemoizedFn } from 'ahooks'

interface EllipsisTextProps {
  className?: string
  children: React.ReactNode
  onEllipsis?: (isEllipsis: boolean) => void
}

const EllipsisText = ({
  className,
  children,
  onEllipsis,
}: EllipsisTextProps) => {
  const textRef = useRef<HTMLSpanElement>(null)

  const checkEllipsis = useMemoizedFn(() => {
    const element = textRef.current
    if (!element) return

    onEllipsis?.(element.scrollWidth > element.clientWidth)
  })

  useEffect(() => {
    checkEllipsis()
  }, [children, checkEllipsis])

  return (
    <span
      ref={textRef}
      className={cls`block min-w-0 flex-1 truncate text-left ${className}`}
      onMouseEnter={checkEllipsis}
    >
      {children}
    </span>
  )
}

export default EllipsisText

