import SignInGoogle from '@/contexts/AuthContext/_blocks/SignInGoogle'
import { IS_PROD } from '@/helpers/env'
import { getServerSideData } from '@/helpers/server-data'
import { withLayouts } from 'react-dx'

import AntdLayout from '../_layout/layouts/AntdLayout'

export default withLayouts(SignInGoogle, [AntdLayout])

export const getServerSideProps = getServerSideData({
  cacheControl: !!IS_PROD,
})(async (_) => {
  return {}
})

import { AntdContextProvider } from '@/contexts/AntdContext'
import { useLocale } from '@/hooks/routes'
import { antdIconPrefix, antdPrefix } from '@/pages/pollo.ai/_constant/theme'
import { figmaTheme } from '@/pages/pollo.ai/_styles/figma-theme'
import { variableInterFont } from '@/styles/next-font-variables'
import { ConfigProvider, theme } from 'antd'
import enUSIntl from 'antd/locale/en_US'
import React from 'react'

import DynamicContextHolder from './DynamicContextHolder'

export interface IAntdLayoutProps {
  children: React.ReactNode
}

const AntdLayout: React.FC<IAntdLayoutProps> = (props) => {
  const { children } = props
  const locale = useLocale()

  return (
    <AntdContextProvider>
      <ConfigProvider
        prefixCls={antdPrefix}
        iconPrefixCls={antdIconPrefix}
        locale={enUSIntl}
        direction={locale === 'ar' ? 'rtl' : 'ltr'}
        theme={{
          algorithm: [theme.darkAlgorithm],
          token: {
            fontFamily: `var(${variableInterFont})`,
            fontSize: 16,
            colorPrimary: figmaTheme.primary.dark,
            colorText: figmaTheme.text.dark,
            colorTextLightSolid: figmaTheme.text.dark,
            colorBgBase: figmaTheme.bgBase.dark,
            colorBgContainer: figmaTheme.bgBase.dark,
          },
          components: {
            Form: {
              itemMarginBottom: 24,
            },
            Divider: {
              colorText: figmaTheme.textQuaternary.dark,
              textPaddingInline: '0.75rem',
              verticalMarginInline: '6px',
            },
            Select: {
              hoverBorderColor: figmaTheme.borderHover.dark,
              activeBorderColor: figmaTheme.borderActive.dark,
              colorText: figmaTheme.text.dark,
              optionSelectedBg: figmaTheme.bgHover.dark,
              optionSelectedColor: figmaTheme.primary.dark,
              optionSelectedFontWeight: '',
              colorTextPlaceholder: figmaTheme.textSecondary.dark,
            },
            Radio: {
              colorTextDisabled: figmaTheme.textDisabled.dark,
            },
            Dropdown: {
              colorBgElevated: figmaTheme.bgContainer.dark,
              controlItemBgHover: figmaTheme.bgActive.dark,
              padding: 4,
              controlPaddingHorizontal: 0,
              paddingBlock: 0,
              colorText: figmaTheme.textTertiary.dark,
            },
            Drawer: {
              colorBgElevated: figmaTheme.bgContainer.dark,
            },
            Slider: {
              colorTextDescription: figmaTheme.textQuaternary.dark,
              railBg: figmaTheme.border.dark,
              railHoverBg: figmaTheme.border.dark,
              trackBg: figmaTheme.text.dark,
              trackHoverBg: figmaTheme.text.dark,
              handleColor: figmaTheme.text.dark,
              handleActiveColor: figmaTheme.text.dark,
              handleActiveOutlineColor: figmaTheme.other4.dark,
              colorPrimaryBorderHover: figmaTheme.text.dark,
              handleSize: 14,
              handleSizeHover: 14,
              handleLineWidth: 3,
              handleLineWidthHover: 3,
            },
            Input: {
              fontSize: 14,
              colorBorder: 'transparent',
              hoverBorderColor: figmaTheme.border.dark,
              activeBorderColor: figmaTheme.border.dark,
              colorText: figmaTheme.text.dark,
              colorTextPlaceholder: figmaTheme.textQuaternary.dark,
              colorTextDescription: figmaTheme.textTertiary.dark,
              activeShadow: '',
              colorBgContainer: figmaTheme.bgLayout.dark,
            },
            InputNumber: {
              handleBorderColor: figmaTheme.border.dark,
              handleHoverColor: figmaTheme.text.dark,
              colorBorder: 'transparent',
              hoverBorderColor: figmaTheme.borderHover.dark,
              activeBorderColor: figmaTheme.borderActive.dark,
              colorText: figmaTheme.text.dark,
              colorTextPlaceholder: figmaTheme.textQuaternary.dark,
              colorTextDescription: figmaTheme.textTertiary.dark,
              activeShadow: '',
              colorBgContainer: figmaTheme.bgLayout.dark,
              addonBg: figmaTheme.bgActive.dark,
            },
            Checkbox: {
              colorWhite: figmaTheme.text.dark,
            },
            Progress: {
              defaultColor: figmaTheme.primary.dark,
              remainingColor: figmaTheme.border.dark,
            },
            Modal: {
              headerBg: 'transparent',
              contentBg: figmaTheme.bgContainer.dark,
              colorIcon: figmaTheme.textTertiary.dark,
              colorBgMask: figmaTheme.bgMask.dark,
            },
            Statistic: {
              colorText: figmaTheme.text.dark,
            },
            Tooltip: {
              fontSize: 14,
              colorTextLightSolid: figmaTheme.text.dark,
              colorBgSpotlight: figmaTheme.bgBase.dark,
            },
            Popover: {
              colorBgElevated: figmaTheme.bgContainer.dark,
            },
            Spin: {
              colorBgContainer: 'transparent',
            },
            Segmented: {
              itemColor: figmaTheme.textQuaternary.dark,
              itemSelectedColor: figmaTheme.text.dark,
              itemHoverColor: figmaTheme.text.dark,
              itemSelectedBg: figmaTheme.bgHover.dark,
              trackBg: figmaTheme.bgLayout.dark,
              itemActiveBg: figmaTheme.bgActive.dark,
              itemHoverBg: figmaTheme.bgActive.dark,
              fontSize: 14,
            },
            Upload: {
              padding: 0,
            },
            Button: {
              defaultShadow: '',
              primaryShadow: '',
              dangerShadow: '',
              defaultBg: '',
              defaultActiveBg: '',
              defaultHoverBg: '',
              defaultColor: figmaTheme.textSecondary.dark,
              defaultBorderColor: figmaTheme.border.dark,
              colorPrimaryHover: figmaTheme.primaryHover.dark,
              colorBgContainerDisabled: figmaTheme.bgDisabled.dark,
              colorTextDisabled: figmaTheme.textDisabled.dark,
              borderColorDisabled: 'transparent',
            },
            Message: {
              contentBg: figmaTheme.bgContainer.dark,
            },
          },
        }}
      >
        {children}
        <DynamicContextHolder />
      </ConfigProvider>
    </AntdContextProvider>
  )
}

export default AntdLayout

import PublisherStructured from '@/components/Structured/PublisherStructured'
import { SiteTopBannersProvider } from '@/contexts/SitePromotionProvider/banners/Provider'
import { SitePromotionPopupsProvider } from '@/contexts/SitePromotionProvider/popups/Provider'
import { useCookieConsent } from '@/hooks/cookieConsent/cookieConsent'
import { useI18nHtmlDir, useI18nHtmlLang } from '@/hooks/i18n'
import { SOCIAL_LINKS } from '@/pages/pollo.ai/_helpers/socialLinks'
import { useLingui } from '@lingui/react/macro'
import React from 'react'
import { withLayouts } from 'react-dx'

import type { ICookieConsentProps } from '@/hooks/cookieConsent/cookieConsent'
import type { ICommonLayoutProps } from '@/layouts/type'

import { promotionPopupsConfig } from '../_components/PromotionPopups/config'
import { topBannersConfig } from '../_components/TopBanners/config'
import Footer from './Footer'
import { FooterTab } from './FooterTab'
import Header from './Header'
import { usePrefetchComponents } from './layouts/hooks/use-prefetch-components'

/** 带 Header/Footer 的 Layout */
function DefaultLayout(props: ICommonLayoutProps) {
  const {
    minHScreen = true,
    mainOnly,
    headerProps,
    footerProps,
    mainProps,
    children,
    locales,
    hasH5BottomTab = true,
  } = props

  const [darkMode, setDarkMode] = useState(false)
  const [staticOrigin] = useOrigin()

  useI18nHtmlDir()
  useI18nHtmlLang()

  usePrefetchComponents()

  useEffect(() => {
    window.getDarkMode = () => darkMode
    window.setDarkMode = setDarkMode
    window.toggleDarkMode = () => {
      setDarkMode(!darkMode)
    }
  }, [darkMode, setDarkMode])
  const { t } = useLingui()
  const sections: ICookieConsentProps['sections'] = useMemo(() => {
    return [
      {
        title: t`We use cookies `,
        description: t`This site makes use of diverse cookies to ensure an enhanced user experience.`,
      },
      {
        title: t`Strictly Required Cookies`,
        description: t`These cookies are vital for our site to operate correctly and comply with regulations. They do not require your explicit consent.`,
        linkedCategory: 'necessary',
      },
      {
        title: t`Audience Measurement`,
        description: t`Track visitor interactions, including visits, page views, and user activity to comprehend site usage and return rate.`,
        linkedCategory: 'advertisement',
      },
      {
        title: t`Third-Party Platforms`,
        description: t`Monitor your engagements with our social networks and advertisements, allowing us to deliver personalized content based on your interests.`,
        linkedCategory: 'analytics',
      },
      {
        title: t`Functionality`,
        description: t`These cookies enable us to customize your browsing experience by remembering your preferences.`,
        linkedCategory: 'functional',
      },
    ]
  }, [t])
  useCookieConsent({
    title: t`We use cookies`,
    description: t`We use cookies to enhance your browsing experience. By continuing to use our website, you consent to the use of cookies.`,
    preferencesTitle: t`Manage Your Cookie Preferences`,
    acceptAllBtn: t`Accept  All`,
    showPreferencesBtn: t`Set up`,
    layout: 'box',
    position: 'middle center',
    savePreferencesBtn: t`Confirm selected preferences`,
    sections,
  })

  const mainDom = (
    <main
      {...mainProps}
      className={tw`
        ${minHScreen && 'flex-1'}
        ${mainProps?.className}
      `}
    >
      {children}
    </main>
  )
  return (
    <>
      <PublisherStructured
        name={'Pollo AI'}
        origin={staticOrigin}
        contactPoint={[
          {
            '@type': 'ContactPoint',
            'email': 'support@pollo.ai',
            'contactType': 'Technical support',
          },
        ]}
        sameAs={[
          SOCIAL_LINKS.x,
          SOCIAL_LINKS.facebook,
          SOCIAL_LINKS.instagram,
          SOCIAL_LINKS.youtube,
          SOCIAL_LINKS.tiktok,
          SOCIAL_LINKS.discord,
        ]}
      />
      {headerProps !== false && !mainOnly ? (
        <Header
          locales={
            locales || [
              'en',
              'ja-JP',
              'es-ES',
              'fr-FR',
              'pt-PT',
              'it-IT',
              'th-TH',
              'pl-PL',
              'ko-KR',
              'de-DE',
              'ru-RU',
              'da-DK',
              'nb-NO',
              'nl-NL',
              'id-ID',
              'zh-Hant-TW',
              'zh-CN',
              'tr-TR',
              'ar-AE',
            ]
          }
          {...headerProps}
        />
      ) : null}
      {mainDom}
      {footerProps !== false && !mainOnly ? (
        <Footer
          {...footerProps}
          locales={
            locales || [
              'en',
              'ja-JP',
              'es-ES',
              'fr-FR',
              'pt-PT',
              'it-IT',
              'th-TH',
              'pl-PL',
              'ko-KR',
              'de-DE',
              'ru-RU',
              'da-DK',
              'nb-NO',
              'nl-NL',
              'id-ID',
              'zh-Hant-TW',
              'zh-CN',
              'tr-TR',
              'ar-AE',
            ]
          }
        />
      ) : null}
      {hasH5BottomTab && (
        <FooterTab className='sticky inset-x-0 bottom-0 z-[999]' />
      )}
    </>
  )
}

const LayoutsDefaultLayout = withLayouts(DefaultLayout, [
  (props) => {
    return <SiteTopBannersProvider {...props} config={topBannersConfig} />
  },
  (props) => {
    return (
      <SitePromotionPopupsProvider {...props} config={promotionPopupsConfig} />
    )
  },
])

export default LayoutsDefaultLayout

import { trackComponentName } from '@/coco-react/analytics'
import LazyImage from '@/components/LazyImage'
import Link from '@/components/Link'
import { useSessionInfo } from '@/contexts/AuthContext/hooks/sessionInfo'
import {
  ACTIVE_PROMOTION_DISCOUNT_NAME,
  useDiscountPromotion,
} from '@/contexts/PlanContext/hooks/discountPromotion'
import {
  DEFAULT_HEADER_HEIGHT,
  HeaderContainerId,
} from '@/helpers/header/getHeaderContainerHeight'
import { useIdleMounted } from '@/hooks/idle'
import { useDevicePerformance } from '@/hooks/useDevicePerformance'
import { useFireworks } from '@/hooks/useFireworks'
import { useShowClaimCredits } from '@/hooks/userGroup/showClaimCredits'
import { aclonica } from '@/styles/next-font'
import { useLingui } from '@lingui/react'
import { useDebounceFn, useScroll } from 'ahooks'
import React from 'react'

import type { ICommonHeaderProps } from '@/layouts/type'

import { WorkspaceHeader } from '../../_blocks/WorkspaceHeader'
import CreditsInfo from '../../_components/CreditsInfo'
import FollowClaimCredits from '../../_components/FollowClaimCredits'
import PromotionPopups from '../../_components/PromotionPopups'
import TopBanners from '../../_components/TopBanners'
import { usePromotionConfig } from '../../_components/TopBanners/banners/PromotionTopBanner/config'
import { useHeaderContainerObserver } from '../../_hooks/useHeaderContainerObserver'
import { useMobileMenuStore } from './_store/mobileMenuStore'
import Advertisement from './Advertisement'
import HeaderMenu from './HeaderMenu'
import History from './History'
import LoginUserInfo from './LoginUserInfo'
import MessageDrawer from './MessageDrawer'
import Pricing from './Pricing'
import ProjectDropdown from './ProjectDropdown'
import StartForFree from './StartForFree'

export default function Header(props: ICommonHeaderProps) {
  const {
    className,
    transparentOnScrollTop0 = false,
    showHeaderMenu = true,
    showLoginUserInfo = true,
    stayOnPage = false,
    locales,
    showCreditsInfo = true,
    isCompactMenu = false,
    showProjectList: _showProjectList = false, // 已废弃：ProjectDropdown 已移到全局 Header
    showAdvertisement = false,
  } = props
  const scroll = useScroll()
  const {
    headerMenuOpen: isMobileMenuOpen,
    setHeaderMenuOpen: setIsMobileMenuOpen,
  } = useMobileMenuStore()
  const { canShow: canShowClaimCredits } = useShowClaimCredits()

  const logoRef = useRef<HTMLSpanElement>(null)
  const isScrollTop0 = useMemo(() => {
    if (transparentOnScrollTop0) {
      return scroll?.top === undefined ? true : scroll.top === 0
    }
    return false
  }, [transparentOnScrollTop0, scroll?.top])
  const headerClass = useMemo(() => {
    if (transparentOnScrollTop0) {
      return `-mb-[52px] ${isScrollTop0 ? 'bg-transparent' : 'bg-f-other-5'}`
    }

    return `bg-f-other-5`
  }, [transparentOnScrollTop0, isScrollTop0])
  useLingui()
  useHeaderContainerObserver()
  const idleMounted = useIdleMounted()

  const { isSignIn } = useSessionInfo()
  const { run: handleLogoClick } = useDebounceFn(
    (e: React.MouseEvent) => {
      tracker.trackEvent(
        {
          name: 'framework_interact',
          interact_type: 'click',
        },
        {
          parsing: {
            event: e,
          },
        },
      )
    },
    { wait: 200, leading: true, trailing: false },
  )

  const { isActivePromotion, promotionIsLoading } = useDiscountPromotion()
  const { headerLogo, banner } = usePromotionConfig()
  const {
    text,
    textClassName,
    boxClassName,
    icon,
    btnClassName,
    btnText,
    bannerLink,
    bgImage,
  } = banner

  // 性能检测与烟花
  const { tier } = useDevicePerformance()
  const { launchFireworks } = useFireworks()

  const { run: handleLogoHover } = useDebounceFn(
    () => {
      if (isActivePromotion && logoRef.current) {
        launchFireworks(logoRef.current, tier)
      }
    },
    { wait: 500, leading: true, trailing: false },
  )

  const isAdPromotionPage =
    showAdvertisement && !promotionIsLoading && isActivePromotion
  return (
    <div
      // 监听header-container的高度, 计算出--header-container-height, 可用于设置sticky的top值
      id={HeaderContainerId}
      className={cls`
        border-f-border-secondary sticky top-0 z-[200] w-full
        ${headerClass}
        ${className}
        ${isMobileMenuOpen && 'z-[2000] !bg-[#12111b]'}
      `}
    >
      {idleMounted && (
        <>
          <TopBanners />
          <PromotionPopups />
        </>
      )}
      <header
        className='relative'
        data-widget-name={
          trackComponentName.framework_widget.value.framework.name
        }
      >
        <div
          style={{
            height: isAdPromotionPage
              ? undefined
              : `${DEFAULT_HEADER_HEIGHT}px`,
          }}
          className={cls`flex items-center justify-between px-4 lg:pe-8 xl:gap-5 ${isAdPromotionPage ? 'py-2' : ''} ${isCompactMenu ? 'border-f-border-secondary border-b border-solid' : ''}`}
        >
          {isAdPromotionPage && bgImage && (
            <LazyImage
              fill
              src={bgImage}
              alt='header'
              className='hidden lg:block'
            />
          )}
          <div className='flex items-center gap-x-2 gap-y-4 sm:gap-4'>
            {isCompactMenu ? (
              <div className='hidden xl:block'>
                <WorkspaceHeader.LeftMenu />
              </div>
            ) : null}
            <div
              className={cls`
                size-6 xl:hidden
                ${isMobileMenuOpen ? 'i-com--x' : 'i-com--menu'}
              `}
              onClick={() => {
                setIsMobileMenuOpen(!isMobileMenuOpen)
              }}
            />
            <Link
              href={'/'}
              className={cls`relative flex items-center ${isActivePromotion ? 'group' : ''}`}
              data-button-name={
                trackComponentName.framework_widget.value.framework.value.logo
                  .name
              }
              onClick={handleLogoClick}
            >
              <span
                ref={logoRef}
                onMouseEnter={handleLogoHover}
                className={cls`i-cus--pol-logo h-7 w-[85px] text-white ${aclonica.className}`}
              />
              {isActivePromotion && headerLogo?.hoverImage}
            </Link>
          </div>
          {showAdvertisement ? (
            <div className={cls`hidden lg:block`}>
              {promotionIsLoading ? null : !isActivePromotion ? (
                <Advertisement />
              ) : (
                <div
                  className={cls`relative z-[1] mx-auto hidden items-center justify-center gap-6 sm:flex ${boxClassName}`}
                >
                  <div className='flex items-center gap-2'>
                    {icon}
                    <p
                      className={cls`text-center md:text-start ${textClassName}`}
                    >
                      {text}
                    </p>
                  </div>
                  <Link
                    data-button-name={ACTIVE_PROMOTION_DISCOUNT_NAME}
                    href={bannerLink || '/pricing'}
                    className={cls`flex items-center justify-center rounded-full text-center ${btnClassName}`}
                  >
                    <span className='sr-only hidden md:not-sr-only md:block'>
                      {btnText}
                    </span>
                    <CssIcon className='ltr:i-com--right-arrow rtl:i-com--left-arrow block size-4 transition-transform duration-150 md:hidden ltr:after:translate-x-1 hover:ltr:after:translate-x-2 rtl:after:-translate-x-1 hover:rtl:after:-translate-x-2' />
                  </Link>
                </div>
              )}
            </div>
          ) : null}
          <div
            className={cls`user-valid:xl:gap-4 user-valid:2xl:gap-6 flex items-center gap-x-1.5 gap-y-3 sm:gap-x-4 xl:gap-6 2xl:gap-8`}
          >
            <HeaderMenu
              showHeaderMenu={showHeaderMenu}
              isMobileMenuOpen={isMobileMenuOpen}
              locales={locales}
              showLoginUserInfo={showLoginUserInfo}
              setIsMobileMenuOpen={setIsMobileMenuOpen}
            />
            {showLoginUserInfo ? (
              <>
                {/* 全局 projectId 切换功能，放在积分左侧 */}
                {isSignIn && (
                  <ProjectDropdown
                    className={cls`
                      ${isAdPromotionPage ? 'relative z-[1]' : ''}
                    `}
                  />
                )}

                {isSignIn && <History />}

                {showCreditsInfo && (
                  <>
                    <CreditsInfo
                      className={cls`user-valid:block hidden ${isAdPromotionPage ? 'relative z-[1]' : ''}`}
                    />
                    {canShowClaimCredits && (
                      <FollowClaimCredits
                        className={cls`md:user-valid:flex hidden ${isAdPromotionPage ? 'relative z-[1]' : ''}`}
                      />
                    )}
                  </>
                )}
                {isSignIn && <MessageDrawer />}

                {/* 首页不展示头部链接菜单时，显示定价链接 */}
                {!showHeaderMenu && <Pricing />}

                {/* //不展示头部链接菜单的默认为工具页面，工具页面登录注册不跳转 */}
                <LoginUserInfo
                  locales={locales}
                  stayOnPage={stayOnPage}
                  className={cls`${isAdPromotionPage ? 'relative z-[1]' : ''}`}
                />
                <StartForFree
                  stayOnPage={stayOnPage}
                  className={cls`user-vague:block hidden truncate ${isAdPromotionPage ? 'relative z-[1] text-center' : ''}`}
                />
              </>
            ) : null}
          </div>
        </div>
      </header>
    </div>
  )
}

import type { IBaseLayoutProps } from './base'

export interface ICommonHeaderProps {
  style?: React.CSSProperties
  className?: string
  transparentOnScrollTop0?: boolean
  /** 是否展示头部导航栏菜单 */
  showHeaderMenu?: boolean
  nav?: boolean
  /** 是否展示登录注册按钮 */
  showLoginUserInfo?: boolean
  /** 登录注册是否需要停留到原页面 */
  stayOnPage?: boolean
  locales?: string[] | null
  showCreditsInfo?: boolean
  /** 是否使用紧凑菜单布局 */
  isCompactMenu?: boolean
  /** 是否展示 Project 列表 */
  showProjectList?: boolean
  /** 是否广告页展示广告 */
  showAdvertisement?: boolean
}

export interface ICommonFooterProps {
  style?: React.CSSProperties
  className?: string
  locales?: string[] | null
  nav?: boolean
  /** 是否使用紧凑菜单布局 */
  isCompactMenu?: boolean
}

export interface ICommonLayoutProps extends TInjectBaseLayoutProps {
  /** 为 false 时不渲染 header */
  headerProps?: ICommonHeaderProps | false
  /** 为 false 时不渲染 footer */
  footerProps?: ICommonFooterProps | false
  /** 页面容器节点 props，内部已处理头部缩进 */
  mainProps?: React.HTMLAttributes<HTMLElement>
  /** 只渲染 main 节点，不渲染 header 和 footer */
  mainOnly?: boolean
  // minHScreen 页面最小高度为 100vh，针对页面实际渲染内容低于 100vh 的情况使用, 类型在 IBaseLayoutProps 中
  children?: React.ReactNode
  locales?: string[]
  /** 当前页面是否需要在h5有底部tab栏 */
  hasH5BottomTab?: boolean
}

export type TInjectBaseLayoutProps = Pick<
  IBaseLayoutProps,
  'seo' | 'className' | 'minHScreen'
>

export type ErrorLayoutProps = ICommonLayoutProps & TInjectBaseLayoutProps

export interface ISiteLayoutProps
  extends ICommonLayoutProps,
    TInjectBaseLayoutProps {}

import SiteSpecificContextLayout from '@/layouts/SiteSpecificContextLayout'
import { withLayouts } from 'react-dx'

import DefaultLayout from '../../index'
import AntdLayout from '../AntdLayout'
import RootLayout from '../RootLayout'

/** 给 404 500 页面使用 */
const SimpleLayout = withLayouts(
  (props) => {
    return <DefaultLayout {...props} minHScreen />
  },
  [
    (props) => {
      return <RootLayout {...props} />
    },
    (props) => {
      return <SiteSpecificContextLayout {...props} />
    },
    AntdLayout,
  ],
)

export default SimpleLayout

/* eslint-disable max-lines */
import { trackComponentName } from '@/coco-react/analytics'
import { MENU_LIST_CONST } from '@/pages/pollo.ai/app/_home/LeftMenuLayout/useMenuList'
import { Trans, useLingui } from '@lingui/react/macro'

import type { InFlowType } from '@/components/InFlowLink/types'

export interface MenuItem {
  name: string
  link?: string
  isNofollow?: boolean
  hidden?: boolean
  shouldIndex: boolean // 是否需要自动生成 sortIndex，一般有点击跳转才加，用于上报埋点数据
  sortIndex?: number // 埋点-排序索引
  dataButtonName: string // 埋点-按钮名称
}
export interface MenuListItem extends Omit<MenuItem, 'link'> {
  link: string
  inflow?: InFlowType
}

export interface IconList extends MenuListItem {
  icon: string
  desc: string
