import { DEFAULT_HEADER_HEIGHT } from '@/helpers/header/getHeaderContainerHeight'
import React from 'react'

import { AiList } from '../../../HeaderList/AiList'
import { GroupList } from '../../../HeaderList/GroupList'
import { useHeaderMenu } from '../../menu'

import type { IHeaderMenuProps } from '../..'
export interface IPCMenuProps
  extends Omit<IHeaderMenuProps, 'isMobileMenuOpen'> {
  localesList: {
    abbr: string
    lang: string
    language: string
    locale: string
    languageInEn: string
  }[]
  showLoginUserInfo?: boolean
}

const PCMenu: React.FC<IPCMenuProps> = (props) => {
  const { showLoginUserInfo } = props
  const menuList = useHeaderMenu()
  return (
    <div
      className={cls`hidden items-center text-sm xl:flex xl:gap-6 2xl:gap-8 2xl:text-base`}
    >
      {menuList.map((item) =>
        item.link ? (
          <Link
            rel={item.isNofollow ? 'nofollow' : ''}
            key={item.name}
            href={item.link}
            className={cls`hover:text-f-primary text-white transition`}
            data-button-name={item.dataButtonName}
            data-track-sort={item.sortIndex}
          >
            {item.name}
          </Link>
        ) : item.videoAiList ? (
          <div
            key={item.name}
            style={{
              height: `${DEFAULT_HEADER_HEIGHT}px`,
            }}
            className={cls`
              group/item after:i-com--up after:text-f-text-tertiary flex
              items-center gap-1 after:size-5 after:origin-center after:rotate-180 after:transition-all
            `}
            data-button-name={item.dataButtonName}
            data-track-sort={item.sortIndex}
          >
            <span className='cursor-default'>{item.name}</span>
            <AiList
              aiList={item.videoAiList}
              type='video'
              showLoginUserInfo={showLoginUserInfo}
              col={4}
            />
          </div>
        ) : item.groupTab ? (
          <div
            key={item.name}
            style={{
              height: `${DEFAULT_HEADER_HEIGHT}px`,
            }}
            className={cls`
              group/item after:i-com--up after:text-f-text-tertiary flex
              items-center gap-1 after:size-5 after:origin-center after:rotate-180 after:transition-all
            `}
            data-button-name={item.dataButtonName}
            data-track-sort={item.sortIndex}
          >
            <span className='cursor-default'>{item.name}</span>
            <GroupList
              className={item.className}
              wrapperClassName={item.wrapperClassName}
              groupTab={item.groupTab}
              showLoginUserInfo={showLoginUserInfo}
            />
          </div>
        ) : item.imageAiList ? (
          <div
            key={item.name}
            style={{
              height: `${DEFAULT_HEADER_HEIGHT}px`,
            }}
            className={cls`
              group/item after:i-com--up after:text-f-text-tertiary flex
              items-center gap-1 after:size-5 after:origin-center after:rotate-180 after:transition-all
            `}
          >
            <span className='cursor-default'>{item.name}</span>
            <AiList aiList={item.imageAiList} type='image' />
          </div>
        ) : null,
      )}
    </div>
  )
}
export default PCMenu

