import { DEFAULT_HEADER_HEIGHT } from '@/helpers/header/getHeaderContainerHeight'
import { useLocale } from '@/hooks/routes'
import { Trans } from '@lingui/react/macro'

import type { InFlowType } from '@/components/InFlowLink/types'

export interface ToolListProps {
  groupTab: {
    label: React.ReactNode
    groupHref: string
    icon: string
    dataButtonName?: string // 埋点-按钮名称
    signInGroupHref?: string
    list: {
      name: string
      link: string
      inflow?: InFlowType
      dataButtonName?: string // 埋点-按钮名称
      sortIndex?: number // 埋点-排序
    }[]
    className?: string
  }[]
  showLoginUserInfo?: boolean
  className?: string
  wrapperClassName?: string
}

export const GroupList: React.FC<ToolListProps> = ({
  groupTab,
  showLoginUserInfo,
  className,
  wrapperClassName,
}) => {
  const locale = useLocale()
  return (
    <div
      style={{
        top: `${DEFAULT_HEADER_HEIGHT}px`,
      }}
      className={tw`
        bg-f-bg-container
        border-f-border-secondary absolute end-0 -z-10 mx-4 flex max-w-[1248px] origin-top-left scale-y-0 flex-col overflow-hidden rounded-xl border p-6 opacity-0 shadow-[0_20px_38px_0_rgba(0,0,0,0.48)]
        transition-all group-hover/item:z-10 group-hover/item:translate-y-0 group-hover/item:scale-y-100
        group-hover/item:opacity-100 ${wrapperClassName}
      `}
    >
      <div className={tw`grid grid-cols-2 gap-16 ${className}`}>
        {groupTab.map((subItem) => (
          <div key={subItem.groupHref} className='flex flex-col'>
            <p
              className={cls`text-f-primary border-f-border-secondary flex items-center gap-2 border-b pb-4 font-semibold ${subItem.icon} before:size-6 before:shrink-0`}
            >
              {subItem.label}
            </p>
            <div
              className={cls`pt-4 ${subItem.className ? subItem.className : 'flex flex-col gap-2'}`}
            >
              {subItem.list.map((tool) => {
                return (
                  <InFlowLink
                    inFlow={tool.inflow}
                    key={tool.link}
                    href={tool.link}
                    className='text-f-text-secondary hover:text-f-primary hover:bg-f-bg-base rounded px-2 py-1.5 text-sm'
                    data-button-name={tool.dataButtonName}
                    data-track-sort={tool.sortIndex}
                  >
                    {tool.name}
                  </InFlowLink>
                )
              })}
            </div>
            <Link
              href={subItem.groupHref}
              className={cls`text-f-primary ${locale === 'ar' ? 'after:i-com--left' : 'after:i-com--right'} flex items-center gap-1 px-2 pt-2 text-sm after:size-4 after:transition-all after:duration-300 ltr:hover:after:translate-x-2 rtl:hover:after:-translate-x-2`}
              data-button-name={`${subItem.dataButtonName}_view_more`}
            >
              <Trans>View More</Trans>
            </Link>
          </div>
        ))}
      </div>
    </div>
  )
}

