import { DEFAULT_HEADER_HEIGHT } from '@/helpers/header/getHeaderContainerHeight'
import { ModelMark } from '@/pages/pollo.ai/_components/Fields/ProFormModelFactory/Marks/type'
import StatusTag from '@/pages/pollo.ai/_components/StatusTag'
import { useLingui } from '@lingui/react/macro'

import type { InFlowType } from '@/components/InFlowLink/types'

import ModelTag from '../components/ModelTag'
import { useImageModelData, useVideoModelData } from './data'

export interface AiListProps {
  aiList: {
    name: string
    link: string
    icon: string
    desc: string
    inflow?: InFlowType
    dataButtonName?: string
    sortIndex?: number
    isBeta?: boolean
  }[]
  type: 'image' | 'video'
  showLoginUserInfo?: boolean
  col?: number
}

export const AiList: React.FC<AiListProps> = ({ aiList, type, col = 3 }) => {
  const { t } = useLingui()
  const imageModalData = useImageModelData()
  const videoModalData = useVideoModelData()
  const supportName =
    type === 'image' ? t`Supported Image Models` : t`Supported Video Models`
  const aiModal = type === 'image' ? imageModalData : videoModalData
  return (
    <div
      style={{
        top: `${DEFAULT_HEADER_HEIGHT}px`,
      }}
      className={cls`
        bg-f-bg-container border-f-border-secondary
        absolute end-0 -z-10 mx-4 flex max-w-[1248px] origin-top-left scale-y-0 flex-col overflow-hidden rounded-xl
        border p-6 opacity-0 shadow-[0_20px_38px_0_rgba(0,0,0,0.48)] transition-all
        group-hover/item:z-10 group-hover/item:translate-y-0 group-hover/item:scale-y-100 group-hover/item:opacity-100
      `}
    >
      <div
        className={cls`grid gap-2`}
        style={{
          gridTemplateColumns: `repeat(${col}, minmax(100px, max-content))`,
        }}
      >
        {aiList.map((subItem) => (
          <div
            className={cls`text-f-primary hover:bg-f-bg-base ${type === 'image' ? 'max-w-[520px]' : 'max-w-[338px]'} relative flex flex-col gap-1.5 rounded-lg py-3 pe-4 ps-3 text-sm`}
            key={subItem.link}
            data-button-name={subItem.dataButtonName}
            data-track-sort={subItem.sortIndex}
          >
            <div className='flex'>
              <InFlowLink
                inFlow={subItem.inflow}
                href={subItem.link}
                className={`flex items-center ps-[34px] font-semibold before:absolute before:start-3 before:size-6 ${subItem.icon} after:absolute after:inset-0 after:size-full`}
              >
                {subItem.name}
              </InFlowLink>
              {subItem.isBeta && (
                <StatusTag
                  status={[ModelMark.Beta]}
                  size='small'
                  className='ms-2'
                />
              )}
            </div>
            <p className='text-f-text-tertiary'>{subItem.desc}</p>
          </div>
        ))}
      </div>
      <div className='border-f-border-secondary mt-4 flex flex-col gap-2.5 border-t px-3 pb-2 pt-4'>
        <p className='text-sm font-semibold'>{supportName}</p>
        <div className='flex flex-wrap gap-4 md:gap-x-6 md:gap-y-3'>
          {aiModal.map((a, aIndex) => {
            return (
              <ModelTag
                key={a.href}
                href={a.href}
                label={a.name}
                isHot={a.isHot}
                isNew={a.isNew}
                dataButtonName={a.dataButtonName}
                dataTrackSort={aIndex + 1}
              />
            )
          })}
        </div>
      </div>
    </div>
  )
}

