import { DEFAULT_HEADER_HEIGHT } from '@/helpers/header/getHeaderContainerHeight'
import {
  TabType,
  useFormHistoryTabStore,
} from '@/pages/pollo.ai/app/_store/formHistoryTab'
import React from 'react'

import { MobileAiList } from '../../../HeaderList/AiList/MobileAiList'
import { MobileGroupList } from '../../../HeaderList/GroupList/MobileGroupList'
import { useHeaderMenu } from '../../menu'
import { menuLayoutMobileName } from './constants'

import type { IHeaderMenuProps } from '../..'
export interface IMobileMenuProps extends IHeaderMenuProps {
  localesList: {
    abbr: string
    lang: string
    language: string
    locale: string
    languageInEn: string
  }[]
}

const MobileMenu: React.FC<IMobileMenuProps> = (props) => {
  const { isMobileMenuOpen, localesList, setIsMobileMenuOpen } = props

  const menuList = useHeaderMenu({ isMobile: true })

  const { setActiveTab } = useFormHistoryTabStore()

  const handleLinkClick = useCallback(() => {
    setActiveTab(TabType.Create)
  }, [setActiveTab])

  const pathName = useUrl().pathname

  //路径变化时关闭菜单
  useEffect(() => {
    setIsMobileMenuOpen?.(false)
  }, [pathName, setIsMobileMenuOpen])

  if (!isMobileMenuOpen) {
    return null
  }

  return (
    <div
      id={menuLayoutMobileName}
      style={{
        top: `${DEFAULT_HEADER_HEIGHT}px`,
      }}
      className={cls`
        border-f-border bg-f-other-5 absolute inset-0
        z-50 block h-[calc(100vh-var(--header-container-height,52px))]
        overflow-y-auto border-t px-6 pb-10 pt-4 xl:hidden
      `}
    >
      <div
        onClick={(e) => {
          const target = e.target as HTMLElement
          if (target.closest('a')) {
            setIsMobileMenuOpen?.(false)
          }
        }}
        className='space-y-4 text-sm font-semibold'
      >
        {menuList.map((item) =>
          item.link ? (
            <React.Fragment key={item.name}>
              <Link
                rel={item.isNofollow ? 'nofollow' : ''}
                href={item.link}
                className={cls`
                    border-f-border
                    hover:text-f-primary
                    flex items-center gap-1.5 border-b pb-4
                    before:size-6 ${item.mobileMenuIcon}
                  ${item.isDarkColor ? 'text-f-text-tertiary' : 'text-f-text-secondary'}
                `}
                data-button-name={item.dataButtonName}
                data-track-sort={item.sortIndex}
                onClick={handleLinkClick}
              >
                {item.name}
              </Link>
            </React.Fragment>
          ) : item.videoAiList ? (
            <React.Fragment key={item.name}>
              <MobileAiList
                name={item.name}
                mobileMenuIcon={item.mobileMenuIcon}
                aiList={item.videoAiList}
                type='video'
                dataButtonName={item.dataButtonName}
                dataTrackSort={item.sortIndex}
                linkClick={handleLinkClick}
              />
            </React.Fragment>
          ) : item.groupTab ? (
            <React.Fragment key={item.name}>
              <MobileGroupList
                mobileMenuIcon={item.mobileMenuIcon}
                name={item.name}
                groupTab={item.groupTab}
                dataButtonName={item.dataButtonName}
                dataTrackSort={item.sortIndex}
                linkClick={handleLinkClick}
              />
            </React.Fragment>
          ) : item.imageAiList ? (
            <React.Fragment key={item.name}>
              <MobileAiList
                name={item.name}
                aiList={item.imageAiList}
                mobileMenuIcon={item.mobileMenuIcon}
                type='image'
                dataButtonName={item.dataButtonName}
                dataTrackSort={item.sortIndex}
                linkClick={handleLinkClick}
              />
            </React.Fragment>
          ) : null,
        )}
        {/* <MobileLocalesList localesList={localesList} /> */}
      </div>
    </div>
  )
}
export default MobileMenu

