import { DEFAULT_HEADER_HEIGHT } from '@/helpers/header/getHeaderContainerHeight'
import { useLocale } from '@/hooks/routes'
import LeftMenuPrimitive from '@/pages/pollo.ai/_components/MenuLayout/LeftMenu'
import { useSiteTopBannersStore } from '@/pages/pollo.ai/_components/TopBanners/_core/hook'
import { useClickAway } from 'ahooks'
import { Button, Drawer } from 'antd'
import { useStore } from 'zustand'

export function LeftMenu({
  isCompactMenu = false,
}: {
  isCompactMenu?: boolean
}) {
  const [open, setOpen] = useState(false)
  const drawerRef = useRef<HTMLDivElement>(null)
  const buttonRef = useRef<HTMLButtonElement>(null)
  const store = useSiteTopBannersStore()
  const currentBanner = useStore(store, (state) => {
    if (!state.openKey) {
      return null
    }
    return state.promotions[state.openKey]
  })
  const height = useStore(store, (state) => state.renderHeight)
  useClickAway(() => {
    setOpen(false)
  }, [drawerRef, buttonRef])

  const showDrawer = () => {
    setOpen(!open)
  }

  const onClose = () => {
    setOpen(false)
  }

  const topHeight = useMemo(() => {
    if (isCompactMenu) {
      return `${DEFAULT_HEADER_HEIGHT}px`
    }
    return currentBanner
      ? `${DEFAULT_HEADER_HEIGHT + height}px`
      : `${DEFAULT_HEADER_HEIGHT}px`
  }, [currentBanner, height, isCompactMenu])
  const locale = useLocale()
  return (
    <>
      <Button
        type='text'
        className={`hover:!bg-f-text-secondary ${open ? 'i-com--x' : 'i-com--menu'} flex size-7 cursor-pointer items-center justify-center`}
        onClick={showDrawer}
        ref={buttonRef}
      />
      <Drawer
        open={open}
        onClose={onClose}
        placement={locale === 'ar' ? 'right' : 'left'}
        width={'fit-content'}
        rootStyle={{
          height: `calc(100vh - ${topHeight})`,
          top: topHeight,
        }}
        drawerRender={() => (
          <LeftMenuPrimitive
            ref={drawerRef}
            className='!pointer-events-auto !static'
          />
        )}
      />
    </>
  )
}

import { trackComponentName } from '@/coco-react/analytics'
import { useAppUIModal } from '@/contexts/AppUIModalContext'
import { useSiteSpecificContext } from '@/contexts/SiteSpecificContext'
import { DEFAULT_HEADER_HEIGHT } from '@/helpers/header/getHeaderContainerHeight'
import { devLog } from '@/helpers/log'
import { useLocale } from '@/hooks/routes'
import { usePlanCloseRewardCb } from '@/pages/pollo.ai/_hooks/usePlanCloseRewardCb'
import { isMenuActive } from '@/utils/helpers'
import { useLingui } from '@lingui/react/macro'
import { useDebounceFn } from 'ahooks'
import React from 'react'
import { useStore } from 'zustand'

import APIMenuButton from '../../APIMenuButton'
import { DownloadButton } from '../../DownloadButton'
import { useSiteTopBannersStore } from '../../TopBanners/_core/hook'
import UpgradeButton from '../../UpgradeButton'
import { useMenuList } from './_hook'
import CreateButton from './CreateButton'
import style from './index.module.scss'
import { MenuIcon } from './MenuIcon'
import MenuItem, { MenuLine } from './MenuItem'

import type { IMenuItem } from './_hook'

interface IProps {
  className?: string
  ref?: React.RefObject<HTMLDivElement>
}

export const LeftMenu = (props: IProps) => {
  const { className = '', ref } = props
  const { menuList } = useMenuList()
  const url = useUrl()
  const { t } = useLingui()
  const { plan } = useSiteSpecificContext()
  const { hasActivePlan, isAbnormalUser } = plan
  const { upgradePlan } = useAppUIModal()
  const { callback: planCloseRewardCb } = usePlanCloseRewardCb()
  const store = useSiteTopBannersStore()
  const currentBanner = useStore(store, (state) => {
    if (!state.openKey) {
      return null
    }
    return state.promotions[state.openKey]
  })
  const height = useStore(store, (state) => state.renderHeight)
  const { run: onUpgrade } = useDebounceFn(
    (e: React.MouseEvent) => {
      upgradePlan.show({
        closeCb: planCloseRewardCb,
        location: 'Menu',
      })
      tracker.trackEvent(
        {
          name: 'framework_interact',
          interact_type: 'click',
        },
        {
          parsing: {
            event: e,
          },
        },
      )
    },
    { wait: 200, leading: true, trailing: false },
  )

  const checkActiveMenu = (link: string) => {
    return isMenuActive(link, url)
  }

  const { run: onMenuClick } = useDebounceFn(
    (item: IMenuItem, e: React.MouseEvent) => {
      devLog(item)
      tracker.trackEvent(
        {
          name: 'navigation_bar_click',
          sort: item.sortIndex,
        },
        {
          parsing: {
            event: e,
          },
        },
      )
    },
    { wait: 200, leading: true, trailing: false },
  )

  const locale = useLocale()

  const topHeight = useMemo(() => {
    return currentBanner
      ? `${DEFAULT_HEADER_HEIGHT + height}px`
      : `${DEFAULT_HEADER_HEIGHT}px`
  }, [currentBanner, height])

  return (
    <nav
      ref={ref}
      className={tw`${style.navMenu} ${className}  scrollbar bg-f-other-5 group peer fixed z-10 w-[92px] overflow-y-auto px-4 pb-6 transition-[width] ${locale === 'ru' || locale === 'de' ? 'xl:w-[240px]' : 'xl:w-[200px]'}`}
      style={{
        height: `calc(100vh - ${topHeight})`,
        top: topHeight,
      }}
      data-widget-name={
        trackComponentName.framework_widget.value.framework.name
      }
    >
      <div
        className='min-h-fit pb-5 pt-4'
        id='menu-container'
        data-widget-name={
          trackComponentName.navigation.value.home_navigation.name
        }
      >
        <CreateButton />
        {menuList.map((item) => {
          if (item.children) {
            return (
              <React.Fragment key={item.title}>
                <span
                  className={`text-f-text-quaternary mb-[6px] mt-[14px] hidden whitespace-pre-wrap ps-3 xl:flex`}
                >
                  {item.title}
                </span>

                {item.children.map((child) => {
                  return (
                    <MenuItem
                      key={child.title}
                      item={child}
                      isActiveMenu={checkActiveMenu(child.url)}
                      data-button-name={child.buttonName}
                      onMenuClick={onMenuClick}
                    />
                  )
                })}

                {item.bottomLine ? (
                  <MenuLine className={'hidden xl:block'} />
                ) : null}
              </React.Fragment>
            )
          }

          return (
            <MenuItem
              key={item.title}
              item={item}
              isActiveMenu={checkActiveMenu(item.url)}
              data-button-name={item.buttonName}
              onMenuClick={onMenuClick}
            />
          )
        })}
        {/* <CanvasEntryLink expand={expand} /> */}
      </div>
      <div className={`mt-auto flex flex-col gap-1`}>
        <DownloadButton
          className={cls`text-f-text-secondary w-[44px] gap-0 p-0 text-[0] xl:text-sm xl:before:!static`}
          eventName='framework_interact'
          popoverEventName='framework_widget_click'
        />
        <APIMenuButton
          className={cls`text-f-text-secondary w-[44px] gap-0 p-0 text-[0] xl:text-sm xl:before:!static`}
        />
        {!hasActivePlan && (
          <UpgradeButton
            onClick={onUpgrade}
            iconClassName='size-5'
            className={`w-[44px] gap-0 bg-transparent bg-none p-0 xl:w-auto xl:gap-2 [&_span.i-cus--pol-crown]:bg-gradient-to-r [&_span.i-cus--pol-crown]:from-[#C721FF] [&_span.i-cus--pol-crown]:to-[#FF3466] xl:[&_span.i-cus--pol-crown]:bg-none [&_span.truncate]:text-[0] xl:[&_span.truncate]:text-sm`}
            buttonText={t`Upgrade Now`}
            data-button-name={
              trackComponentName.framework_widget.value.framework.value
                .upgrade_now.name
            }
          />
        )}
        <MenuIcon
          className={cls`text-f-text-secondary w-[44px] gap-0 p-0 text-[0] xl:text-sm xl:before:!static`}
        />
      </div>
    </nav>
  )
}
export default LeftMenu

import { trackComponentName } from '@/coco-react/analytics'
import { AGENT_APP_PATH } from '@/pages/pollo.ai/app/ai-agent/constants'
import { useLingui } from '@lingui/react/macro'
import { useMemoizedFn } from 'ahooks'

interface IMenuItemBase {
  title: string
  url: string
  icon: React.ReactNode
  isBeta?: boolean
  isNofollow?: boolean
  /** 单个菜单下面分界线 */
  bottomLine?: boolean
  shouldIndex: boolean // 是否需要自动生成 sortIndex，一般有点击跳转才加，用于上报埋点数据
  sortIndex?: number // 埋点-排序索引
}

interface IMenuItemWithChildren extends IMenuItemBase {
  children: IMenuItem[]
  buttonName?: string // 埋点-控件名称
}

interface IMenuItemWithoutChildren extends IMenuItemBase {
  children?: IMenuItem[]
  buttonName: string // 埋点-控件名称
}

export type IMenuItem = IMenuItemWithChildren | IMenuItemWithoutChildren

const homeNavigation = trackComponentName.navigation.value.home_navigation

// 自动生成 sortIndex 的工具函数
const generateSortIndex = (items: IMenuItem[]): IMenuItem[] => {
  let currentIndex = 1

  const processItems = (menuItems: IMenuItem[]): IMenuItem[] => {
    return menuItems.map((item) => {
      const processedItem = { ...item }
      // 如果 shouldIndex 为 true，则自动生成 sortIndex
      if (item.shouldIndex) {
        processedItem.sortIndex = currentIndex++
      }
      // 递归处理子菜单
      if (item.children) {
        processedItem.children = processItems(item.children)
      }
      return processedItem
    })
  }

  return processItems(items)
}

// 提取所有 URL 的工具函数
const extractAllUrls = (
  items: IMenuItem[],
  excludeUrls?: string[],
): string[] => {
  const urls: string[] = []

  const traverse = (menuItems: IMenuItem[]) => {
    menuItems.forEach((item) => {
      // 添加当前项的 URL（如果不在排除列表中）
      if (!excludeUrls || !excludeUrls.includes(item.url)) {
        urls.push(item.url)
      }

      // 如果有子菜单，递归提取
      if (item.children && item.children.length > 0) {
        traverse(item.children)
      }
    })
  }

  traverse(items)
  return urls
}

export const useMenuList = () => {
  const { t } = useLingui()

  const menuListTemplate: IMenuItem[] = [
    {
      title: t`Home`,
      url: '/',
      icon: 'before:i-cus--pol-home',
      buttonName: homeNavigation.value.home.name,
      shouldIndex: true,
    },
    {
      title: t`Explore`,
      url: '/explore',
      icon: 'before:i-cus--pol-community',
      isNofollow: true,
      buttonName: homeNavigation.value.explore.name,
      shouldIndex: true,
    },
    {
      title: t`Assets`,
      url: '/assets',
      icon: 'before:i-cus--pol-projects',
      buttonName: homeNavigation.value.my_creations.name,
      shouldIndex: true,
    },
    {
      title: t`Creation`,
      url: '/app/ai-video',
      icon: 'before:i-cus--pol-video-files',
      shouldIndex: false,
      children: [
        /** /video-studio > /app/ai-video 紧急修改地址 用的rewrites 在 packages/hosts/src/sites/pollo.ai.ts 中 */
        {
          title: t`AI Video`,
          url: '/app/ai-video',
          icon: 'before:i-cus--pol-video-to-video',
          buttonName: homeNavigation.value.ai_video.name,
          shouldIndex: true,
        },
        /** /image-studio > /app/ai-image 紧急修改地址 用的rewrites 在 packages/hosts/src/sites/pollo.ai.ts 中 */
        {
