export interface IHeaderMenuProps {
  isMobileMenuOpen: boolean
  callback?: () => void
  showHeaderMenu?: boolean
  locales?: string[] | null
  showLoginUserInfo?: boolean
  setIsMobileMenuOpen?: (open: boolean) => void
}

const HeaderMenu: React.FC<IHeaderMenuProps> = (props) => {
  const {
    isMobileMenuOpen,
    showHeaderMenu,
    locales,
    setIsMobileMenuOpen,
    showLoginUserInfo,
  } = props
  const localesList = locales
    ? POLLO_SUPPORTED_LANGUAGES.filter((item) => locales?.includes(item.locale))
    : POLLO_SUPPORTED_LANGUAGES
  const menuRef = useRef<HTMLDivElement>(null)
  useContainerTrackCollector(menuRef, {
    name: 'navigation_bar_click',
  })
  return (
    <div
      ref={menuRef}
      data-widget-name={
        trackComponentName.navigation.value.index_navigation.name
      }
      className={cls`${showHeaderMenu ? '' : 'xl:hidden'}`}
    >
      <PCMenu localesList={localesList} showLoginUserInfo={showLoginUserInfo} />
      <MobileMenu
        localesList={localesList}
        isMobileMenuOpen={isMobileMenuOpen}
        setIsMobileMenuOpen={setIsMobileMenuOpen}
      />
    </div>
  )
}
export default HeaderMenu

