    },
  ]

  const newMenuList = menuList.filter((item) => {
    if (item.hidden) {
      return false
    }

    if (item.list) {
      item.list = item.list.filter((subItem) => !subItem.hidden)
      return item.list.length > 0
    }

    return true
  })

  generateSortIndex(newMenuList)

  return newMenuList
}

