     5→} from '@/pages/pollo.ai/app/_store/formHistoryTab'
     6→import React from 'react'
     7→
     8→import { MobileAiList } from '../../../HeaderList/AiList/MobileAiList'
     9→import { MobileGroupList } from '../../../HeaderList/GroupList/MobileGroupList'
    10→import { useHeaderMenu } from '../../menu'
    11→import { menuLayoutMobileName } from './constants'
    12→
    13→import type { IHeaderMenuProps } from '../..'
    14→export interface IMobileMenuProps extends IHeaderMenuProps {
    15→  localesList: {
    16→    abbr: string
    17→    lang: string
    18→    language: string
    19→    locale: string
    20→    languageInEn: string
    21→  }[]
    22→}
    23→
    24→const MobileMenu: React.FC<IMobileMenuProps> = (props) => {
    25→  const { isMobileMenuOpen, localesList, setIsMobileMenuOpen } = props
    26→
    27→  const menuList = useHeaderMenu({ isMobile: true })
    28→
    29→  const { setActiveTab } = useFormHistoryTabStore()
    30→
    31→  const handleLinkClick = useCallback(() => {
    32→    setActiveTab(TabType.Create)
    33→  }, [setActiveTab])
    34→
    35→  const pathName = useUrl().pathname
    36→
    37→  //路径变化时关闭菜单
    38→  useEffect(() => {
    39→    setIsMobileMenuOpen?.(false)
    40→  }, [pathName, setIsMobileMenuOpen])
    41→
    42→  if (!isMobileMenuOpen) {
    43→    return null
    44→  }
    45→
    46→  return (
    47→    <div
    48→      id={menuLayoutMobileName}
    49→      style={{
    50→        top: `${DEFAULT_HEADER_HEIGHT}px`,
    51→      }}
    52→      className={cls`
    53→        border-f-border bg-f-other-5 absolute inset-0
    54→        z-50 block h-[calc(100vh-var(--header-container-height,52px))]
    55→        overflow-y-auto border-t px-6 pb-10 pt-4 xl:hidden
    56→      `}
    57→    >
    58→      <div
    59→        onClick={(e) => {
    60→          const target = e.target as HTMLElement
    61→          if (target.closest('a')) {
    62→            setIsMobileMenuOpen?.(false)
    63→          }
    64→        }}
    65→        className='space-y-4 text-sm font-semibold'
    66→      >
    67→        {menuList.map((item) =>
    68→          item.link ? (
    69→            <React.Fragment key={item.name}>
    70→              <Link
    71→                rel={item.isNofollow ? 'nofollow' : ''}
    72→                href={item.link}
    73→                className={cls`
    74→                    border-f-border
    75→                    hover:text-f-primary
    76→                    flex items-center gap-1.5 border-b pb-4
    77→                    before:size-6 ${item.mobileMenuIcon}
    78→                  ${item.isDarkColor ? 'text-f-text-tertiary' : 'text-f-text-secondary'}
    79→                `}
    80→                data-button-name={item.dataButtonName}
    81→                data-track-sort={item.sortIndex}
    82→                onClick={handleLinkClick}
    83→              >
    84→                {item.name}
    85→              </Link>
    86→            </React.Fragment>
    87→          ) : item.videoAiList ? (
    88→            <React.Fragment key={item.name}>
    89→              <MobileAiList
    90→                name={item.name}
    91→                mobileMenuIcon={item.mobileMenuIcon}
    92→                aiList={item.videoAiList}
    93→                type='video'
    94→                dataButtonName={item.dataButtonName}
    95→                dataTrackSort={item.sortIndex}
    96→                linkClick={handleLinkClick}
    97→              />
    98→            </React.Fragment>
    99→          ) : item.groupTab ? (
   100→            <React.Fragment key={item.name}>
   101→              <MobileGroupList
   102→                mobileMenuIcon={item.mobileMenuIcon}
   103→                name={item.name}
   104→                groupTab={item.groupTab}
   105→                dataButtonName={item.dataButtonName}
   106→                dataTrackSort={item.sortIndex}
   107→                linkClick={handleLinkClick}
   108→              />
   109→            </React.Fragment>
