import { figmaTheme } from '@/pages/pollo.ai/_styles/figma-theme'
import { ConfigProvider, Tooltip } from 'antd'

import type { TooltipProps } from 'antd'

export interface IIconWithTooltipProps {
  children: JSX.Element
  showTooltip?: boolean
  tooltipProps?: TooltipProps
}
function IconWithTooltip(props: IIconWithTooltipProps) {
  const { children, showTooltip, tooltipProps } = props
  if (!showTooltip) {
    return children
  }
  return (
    <ConfigProvider
      theme={{
        components: {
          Tooltip: {
            fontSize: 12,
          },
        },
      }}
    >
      <Tooltip
        styles={{
          body: {
            color: figmaTheme.textTertiary.dark,
            padding: '4px 8px',
            minHeight: 'auto',
          },
        }}
        {...tooltipProps}
      >
        {children}
      </Tooltip>
    </ConfigProvider>
  )
}

export default IconWithTooltip

